import { instances } from "../../api/workflow/activiti";
import FlowImage from "../../components/Activiti/FlowImage";
export default {
  name: 'Instance',
  data: function data() {
    return {
      rows: [],
      loading: false,
      current: {},
      showImage: false,
      page: {
        // 页码
        start: 0,
        // 每页数据条数
        size: 10,
        // 总数据条数
        total: 0
      }
    };
  },
  components: {
    FlowImage: FlowImage
  },
  mounted: function mounted() {
    this.refresh();
  },
  methods: {
    refresh: function refresh() {
      var _this = this;

      instances({
        order: 'desc',
        latest: true,
        sort: 'startTime',
        size: this.page.size,
        start: this.page.start
      }).then(function (res) {
        //console.log(res)
        _this.rows = res.data.data;
        _this.page.total = res.data.total;
      });
    },
    viewImage: function viewImage(row) {
      this.current.processInstanceId = row.id;
      this.current.processDefinitionId = row.processDefinitionId;
      this.showImage = true;
    },
    // 当前页改变
    pageChangeHandler: function pageChangeHandler(e) {
      this.page.start = e;
      this.refresh();
    },
    // 每页条数改变
    sizeChangeHandler: function sizeChangeHandler(e) {
      this.page.size = e;
      this.page.start = 1;
      this.refresh();
    }
  }
};
