import Vue from 'vue'
import Router from 'vue-router'
import loadView from "@/modules"

Vue.use(Router)
import Layout from "@portal/layout/index";
import Submenu from "@portal/layout/submenu";

export const constantRouterMap = [
    {
        path: '/login',
        meta: { title: '登录', noCache: true },
        //component: (resolve) => require([`@portal/layout/login`], resolve),
        component: (resolve) => require([`../views/indicators/login/login`], resolve),
        hidden: true
    },
    {
        path: '/sso/login',
        meta: { title: '统一登录', noCache: true },
        component: (resolve) => require(['@eladmin/views/ssologin'], resolve),
        hidden: true
    },
    {
        path: '/404',
        component: (resolve) => require(['@eladmin/views/features/404'], resolve),
        hidden: true
    },
    {
        path: '/401',
        component: (resolve) => require(['@eladmin/views/features/401'], resolve),
        hidden: true
    },
    {
        path: '/redirect',
        component: Layout,
        hidden: true,
        children: [
            {
                path: '/redirect/:path*',
                component: (resolve) => require(['@eladmin/views/features/redirect'], resolve)
            }
        ]
    },
    {
        path: '/',
        component: Layout,
        redirect: '/dashboard',
        children: [
            {
                path: 'dashboard',
                //component: (resolve) => require([`@portal/layout/home`], resolve),
                component: (resolve) => require([`../views/indicators/layout/home`], resolve),
                name: 'Dashboard',
                meta: { title: '首页', icon: 'index', affix: true, noCache: true }
            }
        ]
    },
    /* {
        path: '/user',
        component: Layout,
        hidden: false,
        redirect: 'noredirect',
        children: [
            {
                path: 'center',
                component: (resolve) => require(['@eladmin/views/system/user/center'], resolve),
                name: '个人中心',
                meta: { title: '个人中心', icon: 'index', }
            }
        ]
    }, */
    // 
    {
        path: '/searchPage',
        component: Layout,
        hidden: false,
        redirect: 'noredirect',
        children: [
            {
                path: 'index',
                component: (resolve) => require(['../views/indicators/searchPage/index'], resolve),
                name: '搜索',
                meta: { title: '搜索', icon: 'index', affix: false, noCache: true }
            }
        ]
    },
    {
        path: '/basicDataMnt',
        name: "basicDataMnt",
        component: Layout,
        hidden: false,
        meta: { title: "基础数据管理", icon: 'index', affix: false, noCache: true },
        redirect: 'noredirect',
        children: [
            {
                path: 'classify',
                component: (resolve) => require(['../views/indicators/basicDataMnt/classify'], resolve),
                name: '数据分级分类导入',
                meta: { title: '数据分级分类导入', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'masterInfo',
                component: (resolve) => require(['../views/indicators/basicDataMnt/masterInfo'], resolve),
                name: '数据属主信息导入',
                meta: { title: '数据属主信息导入', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'blackList',
                component: (resolve) => require(['../views/indicators/basicDataMnt/blackList'], resolve),
                name: '黑名单设置',
                meta: { title: '黑名单设置', icon: 'index', affix: false, noCache: true }
            }
        ]
    },
    {
        path: '/myProcess',
        name: 'myProcess',
        component: Layout,
        hidden: false,
        meta: { title: "我的流程", icon: 'index', affix: false, noCache: true },
        redirect: 'noredirect',
        children: [
            {
                path: 'myApplication',
                component: (resolve) => require(['../views/indicators/myProcess/myApplication'], resolve),
                name: '我的申请',
                meta: { title: '我的申请', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'pendingMyApproval',
                component: (resolve) => require(['../views/indicators/myProcess/pendingMyApproval'], resolve),
                name: '我的待审批',
                meta: { title: '我的待审批', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'myApproved',
                component: (resolve) => require(['../views/indicators/myProcess/myApproved'], resolve),
                name: '我的已审批',
                meta: { title: '我的已审批', icon: 'index', affix: false, noCache: true }
            }
        ]
    },
    {
        path: '/myShopCar',
        component: Layout,
        hidden: false,
        redirect: 'noredirect',
        children: [
            {
                path: 'index',
                component: (resolve) => require(['../views/indicators/myShopCar/myShopCar'], resolve),
                name: '我的购物车',
                meta: { title: '我的购物车', icon: 'index', affix: false, noCache: true }
            }
        ]
    },
    {
        path: '/dataMonitor',
        component: Layout,
        hidden: false,
        redirect: 'noredirect',
        children: [
            {
                path: 'index',
                component: (resolve) => require(['../views/indicators/dataMonitor/dataMonitor'], resolve),
                name: '平台访问数据统计监控',
                meta: { title: '平台访问数据统计监控', icon: 'index', affix: false, noCache: true }
            }
        ]
    },
    {
        path: '/dataServicePlatform',
        component: Layout,
        hidden: false,
        redirect: 'noredirect',
        meta: { title: "数据服务平台", icon: 'index', affix: false, noCache: true },
        children: [
            {
                path: 'applyForApi',
                component: (resolve) => require(['../views/indicators/dataServicePlatform/applyForApi'], resolve),
                name: '申请开发新API',
                meta: { title: '申请开发新API', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'myAPI',
                component: (resolve) => require(['../views/indicators/dataServicePlatform/myAPI'], resolve),
                name: '我的API',
                meta: { title: '我的API', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'docApply',
                component: (resolve) => require(['../views/indicators/dataServicePlatform/docApply'], resolve),
                name: '文件接口申请',
                meta: { title: '文件接口申请', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'myDoc',
                component: (resolve) => require(['../views/indicators/dataServicePlatform/myDoc'], resolve),
                name: '我的文件',
                meta: { title: '我的文件', icon: 'index', affix: false, noCache: true }
            },
        ]
    },
    {
        path: '/monitorModule',
        component: Layout,
        hidden: false,
        redirect: 'noredirect',
        meta: { title: "服务监控模块", icon: 'index', affix: false, noCache: true },
        children: [
            {
                path: 'apiUsage',
                component: (resolve) => require(['../views/indicators/monitorModule/apiUsage'], resolve),
                name: 'API使用情况',
                meta: { title: 'API使用情况', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'docGeneration',
                component: (resolve) => require(['../views/indicators/monitorModule/docGeneration'], resolve),
                name: '文件生成情况',
                meta: { title: '文件生成情况', icon: 'index', affix: false, noCache: true }
            },
        ]
    },
    /* {
        path: '/administrator',
        component: Layout,
        hidden: false,
        redirect: 'noredirect',
        meta: { title: "中台管理员", icon: 'index', affix: false, noCache: true },
        children: [
            {
                path: 'appManagement',
                component: (resolve) => require(['../views/indicators/administrator/appManagement'], resolve),
                name: 'API使用情况',
                meta: { title: '应用管理', icon: 'index', affix: false, noCache: true }
            },
            {
                path: 'appManagement1',
                component: (resolve) => require(['../views/indicators/administrator/appManagement'], resolve),
                name: '文件生成情况',
                meta: { title: '应用管理', icon: 'index', affix: false, noCache: true }
            },
        ]
    }, */

]

export const filterAsyncRouter = (routers) => { // 遍历后台传来的路由字符串，转换为组件对象
    return routers.filter(router => {
        if (router.component) {
            if (router.component === 'Layout') { // Layout组件特殊处理
                router.component = Layout
            } else if (router.component === 'Submenu' || router.component === 'submenu') {
                router.component = Submenu
            } else {
                // console.log('================filterAsyncRouter>>>', router)
                if (router.path.indexOf('?') > 0) { // add by Suny 20200515 处理参数
                    router.meta.cparam = router.path.substring(router.path.indexOf('?'))
                    router.path = router.path.substring(0, router.path.indexOf('?'))
                }
                const component = router.component
                router.component = loadView(component)
                //console.log('-->>>>>>>>#====',router.component)
            }
        }
        if (router.children && router.children.length) {
            router.children = filterAsyncRouter(router.children)
        }
        return true
    })
}
export default new Router({
    mode: 'history',
    scrollBehavior: () => ({ y: 0 }),
    routes: constantRouterMap
})
