import crudServer from "../../../api/monitor/server";
import CRUD, { presenter, header, form, crud } from "../../../components/Crud/crud";
import rrOperation from "../../../components/Crud/RR.operation";
import crudOperation from "../../../components/Crud/CRUD.operation";
import udOperation from "../../../components/Crud/UD.operation";
import pagination from "../../../components/Crud/Pagination"; // crud交由presenter持有

var defaultCrud = CRUD({
  title: '监控',
  url: 'api/server',
  sort: 'sort,asc',
  crudMethod: _objectSpread({}, crudServer)
});
var defaultForm = {
  id: null,
  address: 'localhost',
  name: null,
  ip: null,
  port: 8777,
  state: null,
  cpuRate: null,
  cpuCore: null,
  memTotal: null,
  memUsed: null,
  diskTotal: null,
  diskUsed: null,
  swapTotal: null,
  swapUsed: null,
  sort: 999
};
export default {
  name: 'ServerMonitor',
  components: {
    pagination: pagination,
    crudOperation: crudOperation,
    rrOperation: rrOperation,
    udOperation: udOperation
  },
  mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  data: function data() {
    return {
      permission: {
        add: ['admin', 'server:add'],
        edit: ['admin', 'server:edit'],
        del: ['admin', 'server:del']
      },
      rules: {
        name: [{
          required: true,
          message: '请输入名称',
          trigger: 'blur'
        }],
        address: [{
          required: true,
          message: '请输入IP',
          trigger: 'blur'
        }],
        port: [{
          required: true,
          message: '请输入访问端口',
          trigger: 'blur',
          type: 'number'
        }]
      }
    };
  },
  created: function created() {
    this.crud.optShow.download = false;
  },
  methods: {
    formatCpuRate: function formatCpuRate(row, column) {
      var value = row.cpuRate;

      if (!value) {
        return 0;
      }

      return Math.floor(value * 10000) / 100 + '%';
    },
    percentNumber: function percentNumber(value, total) {
      if (!value || !total) {
        return 0;
      }

      return value / total * 100;
    },
    percentStatus: function percentStatus(value, total) {
      var percent = this.percentNumber(value, total);

      if (percent < 60) {
        return 'success';
      } else if (percent < 90) {
        return 'warning';
      } else {
        return 'exception';
      }
    },
    convertToGb: function convertToGb(num) {
      if (!num) {
        return '-';
      }

      var unit = 'G';

      if (num > 1024) {
        num = num / 1024;
        unit = 'T';
      }

      num = Math.floor(num * 100) / 100;
      return num + unit;
    },
    formatMem: function formatMem(row, column) {
      return this.convertToGb(row.memUsed) + ' / ' + this.convertToGb(row.memTotal);
    },
    formatDisk: function formatDisk(row, column) {
      return this.convertToGb(row.diskUsed) + ' / ' + this.convertToGb(row.diskTotal);
    },
    formatSwap: function formatSwap(row, column) {
      return this.convertToGb(row.swapUsed) + ' / ' + this.convertToGb(row.swapTotal);
    }
  }
};
