import crudDatabase from "../../../api/mnt/database";
import { testDbConnect } from "../../../api/mnt/connect";
import eForm from "./execute";
import CRUD, { presenter, header, form, crud } from "../../../components/Crud/crud";
import rrOperation from "../../../components/Crud/RR.operation";
import crudOperation from "../../../components/Crud/CRUD.operation";
import udOperation from "../../../components/Crud/UD.operation";
import pagination from "../../../components/Crud/Pagination"; // crud交由presenter持有

var defaultCrud = CRUD({
  title: '数据库',
  url: 'api/database',
  crudMethod: _objectSpread({}, crudDatabase)
});
var defaultForm = {
  id: null,
  name: null,
  jdbcUrl: 'jdbc:mysql://',
  userName: null,
  pwd: null
};
export default {
  name: 'DataBase',
  components: {
    eForm: eForm,
    pagination: pagination,
    crudOperation: crudOperation,
    rrOperation: rrOperation,
    udOperation: udOperation
  },
  mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  data: function data() {
    return {
      currentRow: {},
      selectIndex: '',
      databaseInfo: '',
      loading: false,
      permission: {
        add: ['admin', 'database:add'],
        edit: ['admin', 'database:edit'],
        del: ['admin', 'database:del']
      },
      rules: {
        name: [{
          required: true,
          message: '请输入数据库名称',
          trigger: 'blur'
        }],
        jdbcUrl: [{
          required: true,
          message: '请输入数据库连接地址',
          trigger: 'blur'
        }],
        userName: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }],
        pwd: [{
          required: true,
          message: '请输入数据库密码',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    testConnectDatabase: function testConnectDatabase() {
      var _this = this;

      this.$refs['form'].validate(function (valid) {
        if (valid) {
          _this.loading = true;
          testDbConnect(_this.form).then(function (res) {
            _this.loading = false;

            _this.crud.notify(res ? '连接成功' : '连接失败', res ? 'success' : 'error');
          }).catch(function () {
            _this.loading = false;
          });
        }
      });
    },
    execute: function execute() {
      this.$refs.execute.dialog = true;
    },
    handleCurrentChange: function handleCurrentChange(row) {
      this.currentRow = row;
      this.selectIndex = !row ? null : row.id;
    }
  }
};
