import { del } from "../../../api/mnt/deployHistory";
import CRUD, { presenter, header } from "../../../components/Crud/crud";
import rrOperation from "../../../components/Crud/RR.operation";
import crudOperation from "../../../components/Crud/CRUD.operation";
import pagination from "../../../components/Crud/Pagination"; // crud交由presenter持有

var defaultCrud = CRUD({
  title: '部署历史',
  url: 'api/deployHistory',
  crudMethod: {
    del: del
  }
});
export default {
  name: 'DeployHistory',
  components: {
    pagination: pagination,
    crudOperation: crudOperation,
    rrOperation: rrOperation
  },
  mixins: [presenter(defaultCrud), header()],
  data: function data() {
    return {
      delLoading: false,
      permission: {
        del: ['admin', 'deployHistory:del']
      }
    };
  },
  created: function created() {
    this.crud.optShow = {
      add: false,
      edit: false,
      del: true,
      download: true
    };
  },
  methods: {
    delMethod: function delMethod(id) {
      var _this = this;

      this.delLoading = true;
      del([id]).then(function () {
        _this.delLoading = false;

        _this.$refs[id].doClose();

        _this.crud.dleChangePage(1);

        _this.crud.delSuccessNotify();

        _this.crud.toQuery();
      }).catch(function () {
        _this.delLoading = false;

        _this.$refs[id].doClose();
      });
    }
  }
};
