import crudIndIndicatorInfo, { saveArithmetic } from "../../../../api/indicators/indIndicatorInfo";
import { sqlCheck } from '@datasource/api/datasources/indDatasource';
import category from "../../category/categoryInfo";
import aForm from "./taste/tastePage";
import { getDimListById } from "../../../../api/indicators/indDimension";
export default {
  components: {
    category: category,
    aForm: aForm
  },
  data: function data() {
    return {
      loading: false,
      dialog: false,
      sqlCheck: false,
      limitCheck: false,
      title: '',
      ieCode: '',
      tree: {},
      tags: [],
      dBExpress: [],
      tempArray: [],
      dimArray: [],
      tableData: [{
        checkName: '',
        checkFlag: '',
        checkValue: '',
        isCheck: false
      }],
      sqlTemp: true,
      sqlData: {
        dataSourceId: null,
        sqlText: null,
        ieCode: null
      },
      categoryIdL: '',
      categoryName: ''
    };
  },
  mounted: function mounted() {
    // 删除初始化时第一行数据
    this.tableData.splice(0, 1);
  },
  methods: {
    /* SQL校验的method----start */
    // SQL校验触发事件
    sqlTest: function sqlTest() {
      var _this = this;

      var param = {
        'sqlText': this.sqlData.sqlText
      };
      sqlCheck(param).then(function (data) {
        if (data.code === 0) {
          if (data.data.isCheck == 'Y') {
            _this.$message.success({
              message: 'sql校验成功'
            });

            _this.sqlTemp = false;
            return;
          } else {
            _this.$message.error({
              message: 'sql语法不正确'
            });

            _this.sqlTemp = true;
            return;
          }
        } else {
          _this.$message.error({
            message: 'sql校验失败'
          });

          _this.sqlTemp = true;
          return;
        }
      });
    },
    // SQL校验触发事件
    saveSql: function saveSql() {
      var _this2 = this;

      var param = {
        'ieCode': this.sqlData.ieCode,
        'ruleType': '2',
        'calcRule': this.sqlData.sqlText
      };
      saveArithmetic(param).then(function (data) {
        if (data.code === 0) {
          _this2.$message.success({
            message: '衍生指标计算规则保存成功'
          });

          _this2.sqlCheck = false;
        } else {
          _this2.$message.error({
            message: '衍生指标计算规则保存失败'
          });

          _this2.sqlCheck = false;
        }
      });
    }
    /* SQL校验的method----end */

  }
};
