import Treeselect from '@riophae/vue-treeselect';
import '@riophae/vue-treeselect/dist/vue-treeselect.css';
import { updateStatus, getByIeCode } from "../../../../api/indicators/indIndicatorInfo";
export default {
  name: "checkApprove",
  props: {
    id: null
  },
  components: {
    Treeselect: Treeselect
  },
  dicts: ['IE_PROP', 'IE_TYPE', 'IE_METHOD', 'CALC_FREQ', 'IE_STATUS', 'IE_DATA_UNIT', 'RETENTION'],
  data: function data() {
    return {
      active: null,
      approveDialog: false,
      title: '指标审批',
      gdtitle: '指标审批',
      form: {},
      depts: [] // 部门数组

    };
  },
  watch: {
    id: 'changeData' // 值可以为methods的方法名

  },
  mounted: function mounted() {},
  methods: {
    //监听form中iecode的变化
    changeData: function changeData(value, oldvalue) {
      var _this = this;

      if (this.id) {
        getByIeCode(this.id).then(function (res) {
          if (res.code === 0) {
            _this.form = res.data; //当指标状态为已提交时

            if (_this.form.status === '1') {
              _this.active = 1;
            } //当指标状态为11待科技审批时


            if (_this.form.status === '11') {
              _this.active = 2;
            } //当指标状态为2科技已审批


            if (_this.form.status === '2') {
              _this.active = 2;
            } //当指标状态为4已上线时


            if (_this.form.status === '4') {
              _this.active = 3;
            }
          }
        });
      }

      if (this.id) {
        getByIeCode(this.id).then(function (res) {
          if (res.code === 0) {
            _this.form = res.data;
          }
        });
      }
    },
    checkApprove: function checkApprove(status) {
      var _this2 = this;

      // 审批拒绝必须输入审批意见
      if (status === 3) {
        if (this.form.approvalOpinion == null || this.form.approvalOpinion.trim() == '') {
          this.$message.error({
            message: '审批拒绝必须输入审批意见'
          });
          return false;
        }
      } // 审批通过时判断原先状态进行状态修改


      if (status === 2) {
        if (this.form.status == 1) {
          status = 11;
        }

        if (this.form.status == 11) {
          status = 2;
        }

        if (this.form.status == 2) {
          status = 4;
        }

        if (this.form.status == 4) {
          status = 5;
        }
      }
      /* this.$confirm(`确定审批所选数据吗？`, '提示', {
         confirmButtonText: '确定',
         cancelButtonText: '取消',
         type: 'warning'
       }).then(() => {*/


      var param = [];
      var indIndicatorInfo = {
        'id': this.form.id,
        'ieCode': this.form.ieCode,
        'status': status,
        'approvalOpinion': this.form.approvalOpinion
      };
      param.push(indIndicatorInfo);
      updateStatus(param).then(function (returnData) {
        if (returnData.code === 0) {
          _this2.$message.success({
            message: '操作成功!'
          });

          _this2.approveDialog = false;

          _this2.$emit('success', true);

          _this2.$parent.crud.toQuery();
        } else {
          _this2.$message.error({
            message: '操作失败!错误原因:' + returnData.msg
          });

          _this2.approveDialog = false;
        }
      });
      /* })*/
    }
  }
};
