export default {
  name: "dim-val-el-tree-select",
  props: {
    /* 配置项 */
    props: {
      type: Object,
      default: function _default() {
        return {
          id: 'id',
          // ID字段名
          categoryName: 'title',
          // 显示名称
          children: 'children' // 子级字段名

        };
      }
    },

    /* 选项列表数据(树形结构的对象数组) */
    options: {
      type: Array,
      default: function _default() {
        return [];
      }
    },

    /* 初始值 */
    value: {
      type: [String, Array],
      default: function _default() {
        return null;
      }
    },

    /* 可清空选项 */
    clearable: {
      type: Boolean,
      default: function _default() {
        return true;
      }
    },
    multiple: {
      type: Boolean,
      default: function _default() {
        return false;
      }
    },

    /* 自动收起 */
    accordion: {
      type: Boolean,
      default: function _default() {
        return false;
      }
    },
    placeholder: {
      type: String,
      default: function _default() {
        return "检索关键字";
      }
    }
  },
  data: function data() {
    return {
      filterText: '',
      valueId: this.id,
      // 初始值
      valueTitle: null,
      defaultExpandedKey: []
    };
  },
  mounted: function mounted() {
    this.initHandle();
  },
  methods: {
    // 初始化值
    initHandle: function initHandle() {
      var _this = this;

      if (this.valueId) {
        if (this.multiple) {
          this.$refs.selectTree.setCheckedKeys(this.valueId.split(",")); // 设置默认选中

          this.valueTitle = this.$refs.selectTree.getCheckedNodes().map(function (e) {
            return e[_this.props.categoryName];
          }).join(","); //a[this.props.label]
        } else {
          this.$refs.selectTree.setCurrentKey(this.valueId); // 设置默认选中

          this.valueTitle = this.$refs.selectTree.getNode(this.valueId).data[this.props.categoryName]; // 初始化显示
        }

        this.defaultExpandedKey = [this.valueId]; // 设置默认展开
      }

      this.initScroll();
    },
    // 初始化滚动条
    initScroll: function initScroll() {
      this.$nextTick(function () {
        var scrollWrap = document.querySelectorAll('.el-scrollbar .el-select-dropdown__wrap')[0];
        var scrollBar = document.querySelectorAll('.el-scrollbar .el-scrollbar__bar');
        scrollWrap.style.cssText = 'margin: 0px; max-height: none; overflow: hidden;';
        scrollBar.forEach(function (ele) {
          return ele.style.width = 0;
        });
      });
    },
    // 切换选项
    handleNodeClick: function handleNodeClick(node) {
      if (this.multiple) {
        return;
      }

      console.debug('=====handleNodeClick=======', node);
      this.valueTitle = node[this.props.categoryName];
      this.valueId = node[this.props.id];
      this.$emit('getValue', this.valueId);
      this.defaultExpandedKey = [];
    },
    // 切换选项
    handleCheck: function handleCheck(data, node) {
      var _this2 = this;

      console.debug('======handleCheck======', data, node);
      this.valueId = node.checkedKeys.join(",");
      this.valueTitle = node.checkedNodes.map(function (e) {
        return e[_this2.props.categoryName];
      }).join(",");
      this.$emit('getValue', this.valueId);
      this.defaultExpandedKey = [];
    },
    // 清除选中
    clearHandle: function clearHandle() {
      this.valueTitle = '';
      this.valueId = null;
      this.defaultExpandedKey = [];
      this.clearSelected();
      this.$emit('getValue', null);
    },

    /* 清空选中样式 */
    clearSelected: function clearSelected() {
      var allNode = document.querySelectorAll('#tree-option .el-tree-node');
      allNode.forEach(function (element) {
        return element.classList.remove('is-current');
      });
    },
    filterNode: function filterNode(value, data) {
      if (!value) return true;
      return data.name.indexOf(value) !== -1;
    }
  },
  watch: {
    value: function value() {
      this.valueId = this.id;
      this.initHandle();
    },
    filterText: function filterText(val) {
      this.$refs.selectTree.filter(val);
    }
  }
};
