import crudIndCategory from "../../../api/indicators/indCategory";
var defaultForm = {
  id: null,
  categoryType: null,
  categoryName: null,
  categoryDesc: null,
  parentId: null,
  crtUserCode: null,
  crtOrgCode: null,
  crtDate: null,
  updUserCode: null,
  updOrgCode: null,
  updDate: null
};
export default {
  name: 'IndCategory',
  // components: {pagination, crudOperation, rrOperation, udOperation},
  props: {
    type: {
      type: String,
      default: '1'
    },
    parentId: {
      type: String,
      default: '0'
    }
  },
  // mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  data: function data() {
    return {
      filterText: '',
      defaultProps: {
        children: 'children',
        label: 'name'
      },
      treeData: [{
        id: '1',
        categoryType: '1',
        categoryName: '基础指标',
        children: []
      }, {
        id: '2',
        categoryType: '2',
        categoryName: '衍生指标',
        children: []
      }, {
        id: '3',
        categoryType: '9',
        categoryName: '参数',
        children: []
      }],
      permission: {
        add: ['admin', 'indCategory:add'],
        edit: ['admin', 'indCategory:edit'],
        del: ['admin', 'indCategory:del']
      },
      rules: {
        id: [{
          required: true,
          message: '分类ID不能为空',
          trigger: 'blur'
        }]
      }
    };
  },
  watch: {
    filterText: function filterText(val) {
      this.$refs.tree.filter(val);
    }
  },
  mounted: function mounted() {
    // console.log('=====category---mounted....')
    this.init();
  },
  methods: {
    // 获取数据前设置好接口地址

    /* [CRUD.HOOK.beforeRefresh]() {
       return true
     },*/
    filterNode: function filterNode(value, treeData) {
      if (!value) return true;
      return treeData.categoryName.indexOf(value) !== -1;
    },
    // 点击事件
    handleNodeClick: function handleNodeClick(data) {
      this.$emit('node-click', data);
    },
    init: function init() {
      var _this = this;

      if (this.type == 0) {
        // 查询基础指标分类+信息
        crudIndCategory.treeInfo('1', this.parentId).then(function (res) {
          _this.treeData[0].children = res.data;
        }).catch(function () {
          console.log('');
        }); // 查询衍生指标分类+信息

        crudIndCategory.treeInfo('2', this.parentId).then(function (res) {
          _this.treeData[1].children = res.data;
        }).catch(function () {
          console.log('');
        }); // 查询参数分类+信息

        crudIndCategory.treeInfo('9', this.parentId).then(function (res) {
          _this.treeData[2].children = res.data;
        }).catch(function () {
          console.log('');
        });
      } else if (this.type == 1) {
        this.treeData = [{
          id: '1',
          categoryType: '1',
          categoryName: '基础指标',
          children: []
        }]; // 查询基础指标分类+信息

        crudIndCategory.treeInfo('1', this.parentId).then(function (res) {
          _this.treeData[0].children = res.data;
        }).catch(function () {
          console.log('');
        });
      } else if (this.type == 2) {
        this.treeData = [{
          id: '1',
          categoryType: '2',
          categoryName: '衍生指标',
          children: []
        }]; // 查询衍生指标分类+信息

        crudIndCategory.treeInfo('2', this.parentId).then(function (res) {
          _this.treeData[0].children = res.data;
        }).catch(function () {
          console.log('');
        });
      }
    },
    // 刷新左侧树状列表的方法
    refresh: function refresh() {
      console.log('测试一下');
    }
  }
};
