import crudServer from "../../../api/mnt/serverDeploy";
import { testServerConnect } from "../../../api/mnt/connect";
import { validateIP } from "../../../utils/validate";
import CRUD, { presenter, header, form, crud } from "../../../components/Crud/crud";
import rrOperation from "../../../components/Crud/RR.operation";
import crudOperation from "../../../components/Crud/CRUD.operation";
import udOperation from "../../../components/Crud/UD.operation";
import pagination from "../../../components/Crud/Pagination"; // crud交由presenter持有

var defaultCrud = CRUD({
  title: '服务器',
  url: 'api/serverDeploy',
  crudMethod: _objectSpread({}, crudServer)
});
var defaultForm = {
  id: null,
  name: null,
  ip: null,
  port: 22,
  account: 'root',
  password: null
};
export default {
  name: 'Server',
  components: {
    pagination: pagination,
    crudOperation: crudOperation,
    rrOperation: rrOperation,
    udOperation: udOperation
  },
  mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  data: function data() {
    return {
      accountList: [],
      accountMap: {},
      loading: false,
      permission: {
        add: ['admin', 'serverDeploy:add'],
        edit: ['admin', 'serverDeploy:edit'],
        del: ['admin', 'serverDeploy:del']
      },
      rules: {
        name: [{
          required: true,
          message: '请输入名称',
          trigger: 'blur'
        }],
        ip: [{
          required: true,
          message: '请输入IP',
          trigger: 'blur'
        }, {
          validator: validateIP,
          trigger: 'change'
        }],
        port: [{
          required: true,
          message: '请输入端口',
          trigger: 'blur',
          type: 'number'
        }],
        account: [{
          required: true,
          message: '请输入账号',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    testConnectServer: function testConnectServer() {
      var _this = this;

      this.$refs['form'].validate(function (valid) {
        if (valid) {
          _this.loading = true;
          testServerConnect(_this.form).then(function (res) {
            _this.loading = false;

            _this.$notify({
              title: res ? '连接成功' : '连接失败',
              type: res ? 'success' : 'error',
              duration: 2500
            });
          }).catch(function () {
            _this.loading = false;
          });
        }
      });
    }
  }
};
