import crudIndIndicatorInfo, { saveArithmetic, checkexpression, updateStatuesByIeCode } from "../../../../api/indicators/indIndicatorInfo";
import { sqlCheck } from '@datasource/api/datasources/indDatasource';
import category from "../../category/categoryInfo";
import aForm from "./taste/tastePage";
import { getDimListById } from "../../../../api/indicators/indDimension";
export default {
  components: {
    category: category,
    aForm: aForm
  },
  data: function data() {
    return {
      loading: false,
      arithmeticDialog: false,
      title: '',
      ieCode: '',
      arithtext: ''
    };
  },
  mounted: function mounted() {},
  methods: {
    /* 四则运算的method----start */
    // 点击指标触发
    handleCategoryClick: function handleCategoryClick(data) {
      if (data.categoryType === '88') {
        // 获取指标名称,添加[]后调用insertText方法
        var categoryName = '[' + data.categoryName + ']';
        this.insertText(document.getElementById('arith'), categoryName); // 调用子组件方法刷新左侧树状列表
        // this.$refs.category.refresh();
      }
    },
    // 点击运算符号触发
    handleSymbolClick: function handleSymbolClick(param) {
      this.insertText(document.getElementById('arith'), param);
    },
    // textarea中在鼠标位置插入内容。obj为textarea对象,charvalue为插入内容
    insertText: function insertText(obj, charvalue) {
      obj.focus();
      var leng = obj.value.length;

      if (typeof document.selection !== 'undefined') // ie
        {
          var r = document.selection.createRange();
          r.text = charvalue;
        } else // firefox
        {
          var ops = obj.value.substr(0, obj.selectionStart) + charvalue;
          obj.value = ops + obj.value.substring(obj.selectionStart, leng);
          obj.setSelectionRange(ops.length, ops.length);
        }
    },
    // 试算
    taste: function taste() {
      var _this2 = this;

      // 校验表达式
      var param = {
        'calcRule': document.getElementById('arith').value
      };
      checkexpression(param).then(function (retuData) {
        if (retuData.code === 0) {
          var modules = [];
          modules = retuData.data; // 分割后的指标名称
          // 打开指标试算页面

          var _this = _this2.$refs.tastePage;
          _this.formula = document.getElementById('arith').value;
          _this.modules = modules;
          _this.title = '试算';
          _this.resultData = [];
          _this.dialog = true;
        } else {
          _this2.$message.error({
            message: retuData.msg
          });
        }
      });
    },
    // 取消按钮
    cancel: function cancel() {
      this.arithmeticDialog = false;
    },
    // 重置
    reset: function reset() {
      this.arithtext = '';
    },
    // 保存
    saveAri: function saveAri() {
      var _this3 = this;

      // 校验表达式是否正确
      var param = {
        'calcRule': document.getElementById('arith').value
      };
      checkexpression(param).then(function (retuData) {
        if (retuData.code === 0) {
          var data = {
            'ieCode': _this3.ieCode,
            'ruleType': '1',
            'ruleDesc': document.getElementById('arith').value
          };
          saveArithmetic(data).then(function (data) {
            if (data.code === 0) {
              _this3.$message.success({
                message: '保存成功!'
              });

              _this3.cancel();

              _this3.$emit('func', 'hi');
            } else {
              _this3.$message.error({
                message: data.msg
              });
            }
          });
        } else {
          _this3.$message.error({
            message: retuData.msg
          });
        }
      });
    },
    // 上线
    upLine: function upLine() {
      var _this4 = this;

      // 校验表达式是否正确
      var param = {
        'calcRule': document.getElementById('arith').value
      };
      checkexpression(param).then(function (retuData) {
        if (retuData.code === 0) {
          var data = {
            'ieCode': _this4.ieCode,
            'ruleType': '1',
            'ruleDesc': document.getElementById('arith').value
          };
          saveArithmetic(data).then(function (data) {
            if (data.code === 0) {
              var indIndicatorInfo = {
                'ieCode': _this4.ieCode,
                'status': 4
              };
              updateStatuesByIeCode(indIndicatorInfo).then(function (data) {
                if (data.code === 0) {
                  _this4.$message.success({
                    message: '操作成功!'
                  });

                  _this4.cancel();

                  _this4.$emit('func', 'hi');
                } else {
                  _this4.$message.error({
                    message: data.msg
                  });
                }
              });
            } else {
              _this4.$message.error({
                message: data.msg
              });
            }
          });
        } else {
          _this4.$message.error({
            message: retuData.msg
          });
        }
      });
    }
    /* 四则运算的method----end */

  }
};
