  name: 'TodoList',
  dicts: ['PROCESS_ID'],
  components: {
    FlowImage: FlowImage,
    // checkApprove,
    Processes: Processes
  },
  data: function data() {
    return {
      query: {
        type: '1',
        finished: '',
        processId: '',
        processName: ''
      },
      form: {
        taskId: '',
        assignee: '',
        comment: '',
        parameter: ''
      },
      current: {},
      rows: [],
      loading: false,
      startFlow: false,
      isAssign: false,
      showImage: false,
      businessKey: '',
      users: [],
      page: {
        // 页码
        start: 0,
        // 每页数据条数
        size: 10,
        // 总数据条数
        total: 0
      }
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.refresh();
    getUsers({
      page: 0,
      size: 999,
      sort: 'username,desc',
      deptId: this.user().deptId,
      enabled: true
    }).then(function (res) {
      _this.users = res.content;
    });
  },
  methods: _objectSpread({
