        order: 'desc',
        latest: true,
        sort: 'startTime',
        size: this.page.size,
        start: this.page.start,
        type: this.query.type == '4' || this.query.type == '5' ? '3' : this.query.type,
        finished: this.query.type == '4' ? true : this.query.type == '0' ? '' : false,
        processName: this.query.processName,
        starter: this.query.starter
      }).then(function (res) {
        // console.log(res)
        _this2.rows = res.data.records;
        _this2.page.total = res.data.total;
      });
    },
