import crud from "../../../mixins/crud";
import { reducte } from "../../../api/mnt/deployHistory";
export default {
  mixins: [crud],
  props: {
    appName: {
      type: String,
      default: ''
    }
  },
  data: function data() {
    return {
      submitLoading: false,
      dialog: false,
      history: [],
      radio: '',
      appNames: '',
      selectIndex: ''
    };
  },
  created: function created() {
    var _this = this;

    this.$nextTick(function () {
      _this.init();
    });
  },
  methods: {
    beforeInit: function beforeInit() {
      this.url = 'api/deployHistory';
      this.deployId = this.$parent.deployId;

      if (this.deployId === '') {
        return false;
      }

      this.sort = 'deployDate,desc';
      this.params['deployId'] = this.deployId;
      return true;
    },
    showRow: function showRow(row) {
      this.radio = this.data.indexOf(row);
      this.selectIndex = row.id;
    },
    cancel: function cancel() {
      this.dialog = false;
      this.submitLoading = false;
    },
    doSubmit: function doSubmit() {
      var _this2 = this;

      if (this.selectIndex === '') {
        this.$message.error('请选择要还原的备份');
      } else {
        this.submitLoading = true;
        reducte(JSON.stringify(this.data[this.radio])).then(function (res) {
          _this2.dialog = false;
          _this2.submitLoading = false;
          _this2.appNames = '';

          _this2.$parent.crud.toQuery();
        }).catch(function (err) {
          _this2.submitLoading = false;
          console.log('error:' + err.response.data.message);
        });
      }
    }
  }
};
