import { generator, sync as _sync } from "../../api/generator/generator";
import { downloadFile } from "../../utils/index";
import CRUD, { presenter, header } from "../../components/Crud/crud";
import rrOperation from "../../components/Crud/RR.operation";
import crudOperation from "../../components/Crud/CRUD.operation";
import pagination from "../../components/Crud/Pagination"; // crud交由presenter持有

var defaultCrud = CRUD({
  url: 'api/generator/tables'
});
export default {
  name: 'GeneratorIndex',
  components: {
    pagination: pagination,
    crudOperation: crudOperation,
    rrOperation: rrOperation
  },
  mixins: [presenter(defaultCrud), header()],
  data: function data() {
    return {
      syncLoading: false
    };
  },
  created: function created() {
    console.log('=====router>>', this.$router);
    this.crud.optShow = {
      add: false,
      edit: false,
      del: false,
      download: false
    };
  },
  methods: {
    toGen: function toGen(tableName) {
      var _this = this;

      // 生成代码
      generator(tableName, 0).then(function (data) {
        _this.$notify({
          title: '生成成功',
          type: 'success',
          duration: 2500
        });
      });
    },
    toDownload: function toDownload(tableName) {
      // 打包下载
      generator(tableName, 2).then(function (data) {
        downloadFile(data, tableName, 'zip');
      });
    },
    sync: function sync() {
      var _this2 = this;

      var tables = [];
      this.crud.selections.forEach(function (val) {
        tables.push(val.tableName);
      });
      this.syncLoading = true;

      _sync(tables).then(function () {
        _this2.crud.refresh();

        _this2.crud.notify('同步成功', CRUD.NOTIFICATION_TYPE.SUCCESS);

        _this2.syncLoading = false;
      }).then(function () {
        _this2.syncLoading = false;
      });
    }
  }
};
