    handleRemove(data) {
      //处理删除，level为1表示指标维度分类的操作
      if (data.dimLevel == 1) {
        const $this = this
        this.$confirm('确定要删除该节点吗?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(() => {
          if (data.children.length === 0) {
            crudIndClassification.crud.del([data.id]).then((res) => {
              if (res.code !== 0) {
                $this.$message({
                  type: 'error',
                  message: '删除失败,' + res.msg
                })
              } else {
                $this.$message({
                  type: 'success',
                  message: '删除成功 '
                })
                this.initTreeData()
              }
            }).catch(() => {
              $this.$message({
                type: 'error',
                message: '删除失败 '
              })
            })
          } else {
            $this.$message({
              type: 'error',
              message: '该分类已被引用，不可删除'
            })
          }
        })
      } else if (data.dimLevel == 2) {
        const $this = this
        this.$confirm('确定要删除该节点吗?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
