    addDimensionButton() {
      //新增按钮
      if (this.dimLevel == null || this.dimLevel == undefined) {
        this.$message({
          type: 'error',
          message: '请选择左侧维度分类树！'
        })
        return;
      }
      if (this.dimLevel == 5) {
        this.$message({
          type: 'error',
          message: '维度树最多到5级！'
        })
        return;
      }
      this.addDimension(this.buttonTreeData);
    },
    modifyDimensionButton() {
      //修改按钮
      if (this.dimLevel == null || this.dimLevel == undefined || this.dimLevel == 0) {
        this.$message({
          type: 'error',
          message: '请选择左侧维度分类树！'
        })
        return;
      }

      if (this.buttonTreeData != null && this.buttonTreeData.children && this.buttonTreeData.children.length > 0) {
        this.$message({
          type: 'error',
          message: '该节点下有子节点，不能修改！'
        })
        return;
      }
      this.handleEdit(this.buttonTreeData);
    },
    handleZoom() {
      if (this.zoomDirection == 'left') {
        this.categorySpan = 0;
        this.tableSpan = 24;
        this.zoomDirection = 'right';
      } else {
        this.categorySpan = 7;
        this.tableSpan = 17;
        this.zoomDirection = 'left';
      }
    }

  },
  computed: {
    hasOperatePermission() {
      const authorizes = this.$store.getters && this.$store.getters.authorizes;
      const permissionRoles = ['admin', 'indDimension:del'];
      return authorizes.some(authorize => permissionRoles.includes(authorize));
    }
  }
