.navbar {
  width: 100%;
  display: flex;
  position: relative;
  background: url("/../../assets/image/bg_header.png") 100% 100% no-repeat;
  // background: linear-gradient(90deg, rgba(120, 171, 67, 1) 0%, rgba(136, 191, 77, 1) 100%);
  .portal-header-title {
    left: 155px;
    top: 19px;
    width: 105px;
    height: 33px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: flex-start;
    margin-top: 19px;
    margin-left: 24px;
    .portal-header-title-top {
      left: 0px;
      top: 0px;
      width: 96px;
      height: 22px;
      display: flex;
      color: rgba(255, 255, 255, 1);
      color: rgba(120, 171, 67, 1);
      font-size: 16px;
    }
    .portal-header-title-bottom {
      left: 0px;
      top: 22px;
      width: 105px;
      height: 11px;
      display: flex;
      color: rgba(255, 255, 255, 1);
      color: rgba(120, 171, 67, 1);
      font-size: 8px;
      white-space: nowrap;
      transform: scale(0.65);
      margin-left: -18px;
    }
  }

  .portal-header-search {
    width: 401px;
    height: 32px;
    display: flex;
    justify-content: space-between;
    margin-top: 19px;
    margin-left: 223px;
  }
  ::v-deep .el-autocomplete {
    .el-input__inner {
      border: 1px solid #61bd53;
    }
  }
  .el-input--mini .el-input__inner {
    height: 32px;
  }
  .el-input--mini .el-input__icon {
    height: 28px;
  }
  .right-menu {
    display: flex;
    align-items: center;
    height: 70px;
    position: absolute;
    right: 24px;
    .profile-picture {
      width: 30px;
      height: 30px;
      border-radius: 30px;
      margin-right: 8px;
      display: flex;
      justify-content: center;
      align-items: center;
      color: #202224;
      font-weight: 700;
    }
  }
}
