import request from "@/utils/request";

export default {
    name: 'TaskExportPlan',
    data() {
        return {
          taskNameInput: '',
          dept: '',
          // 表格
          tableData: [],
          queryForm: {
            size: 10,
            page: 1,
            total: 0,
            loading: true
          },
          editDialogVisible: false,
          editForm: {
          /* taskName: '',
            authDeptName: '',
            emailAddr: '',
            sqlText: '',
            isDd: ''*/
          },
        }
    },
    methods: {
      //格式化日期
      formatDate(row, column) {
        // 获取单元格数据
        let data = row[column.property]
        if(data == null) {
          return null
        }
        let dt = new Date(data)
        return dt.getFullYear() + '-' + ((dt.getMonth() + 1)<10?'0'+ (dt.getMonth() + 1): (dt.getMonth() + 1) )+ '-' + dt.getDate() /*+ ' ' + dt.getHours() + ':' + dt.getMinutes() + ':' + dt.getSeconds()*/
      },
      async queryPageList() {
        this.queryForm.loading = true
        let res = await request({
          url: '/datalab/taskPlan/list',
          method: 'post',
          data: {
            pageSize: this.queryForm.size,
            pageNum: this.queryForm.page,
            taskName: this.taskNameInput,
            authDeptName: this.dept
          }
        })
        if (res.data && res.data.records) {
          this.tableData = res.data.records
          this.queryForm.total = res.data.total
          this.queryForm.page = res.data.pages

        }
        this.queryForm.loading = false
        console.log(res)
      },
      // 分页
      handleSizeChange(val) {
        this.queryForm.size = val;
        this.queryPageList();
      },
      handleCurrentChange(val) {
        this.queryForm.page = val;
        this.queryPageList();
      },
      //编辑
      handleEdit(index, row) {
        this.editDialogVisible = true;
        this.editForm = row
        console.log(index);
        console.log(row);
      },
      //终止
      handleStop(index, row) {
        console.log(row)
        this.editForm = row;
        this.$confirm('是否要终止该定时任务?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(() => {
          request({
            url: '/datalab/taskPlan/stop',
            method: 'post',
            data: {
              ...this.editForm,
              taskState: 'STOP'
            }
          }).then((res) => {
            if (res.returnCode === '200') {
              this.$message({
                type: 'success',
                message: res.returnMsg
              });
            } else {
              this.$message({
                type: 'error',
                message: res.returnMsg
              });
            }
            this.queryPageList()
          })
        });
      },
    },
  mounted() {
    this.queryPageList()
  }
}
