export default {
  name: "MyShopCar",
  data() {
    return {
      // 条件
      appCode: "",
      appName: "",
      status: "",
      // 表格
      tableData: [],
      propList: [],
      // 分页
      pageSize: 10,
      currentPage: 1,
      total: 10,
      // 删除确认弹窗
      confirmDialog: false,
      message: "",
      // 当前操作id
      currentId: "",
      // 应用服务关系
      appServiceVis: false,
      activeName: "first",
      diaDataProp: [],
      diaDataList: [],
      curAppCode: "",
    };
  },
  // beforeRouteLeave(to, from, next) {
  //   if (
  //     to.name === "applyForApp" ||
  //     to.name === "appDetail" ||
  //     to.name === "editApp"
  //   ) {
  //     this.$store.commit("SET_KEEPALIVE", true);
  //   } else {
  //     this.$store.commit("SET_KEEPALIVE", false);
  //   }
  //   next();
  // },
  created() {
    // this.$store.commit("SET_KEEPALIVE", true);
    this.propList = [
      {
        prop: "appName",
        label: "应用名称",
      },
      {
        prop: "appCode",
        label: "应用code",
      },
      {
        prop: "description",
        label: "描述",
      },
      {
        prop: "status",
        label: "状态",
        type: "tag",
        tagType: (row) => {
          let type = "";
          if (row.status === "0") {
            type = "info";
          } else if (row.status === "1") {
            type = "warning";
          } else if (row.status === "2") {
            type = "warning";
          } else if (row.status === "3") {
            type = "success";
          } else if (row.status === "4") {
            type = "danger";
          } else if (row.status === "5") {
            type = "warning";
          } else if (row.status === "6") {
            type = "warning";
          } else if (row.status === "7") {
            type = "warning";
          } else if (row.status === "8") {
            type = "";
          }
          return type;
        },
        render: (row) => {
          let status = "";
          if (row.status === "0") {
            status = "已下线";
          } else if (row.status === "1") {
            status = "草稿";
          } else if (row.status === "2") {
            status = "需求已提交";
          } else if (row.status === "3") {
            status = "需求已通过";
          } else if (row.status === "4") {
            status = "需求已驳回";
          } else if (row.status === "5") {
            status = "开发中";
          } else if (row.status === "6") {
            status = "测试中";
          } else if (row.status === "7") {
            status = "已完成待上线";
          } else if (row.status === "8") {
            status = "已上线";
          }
          return status;
        },
      },
      {
        prop: "createDate",
        label: "创建时间",
      },
    ];
  },
  mounted() {
    this.queryList();
  },
  methods: {
    // 查询列表
    queryList() {
      let request = {
        page: this.currentPage,
        size: this.pageSize,
        appCode: this.appCode,
        appName: this.appName,
        status: this.status,
      };
      this.$api.qryAppList(request).then((res) => {
        console.log(res);
        this.tableData = res.data.records;
        this.total = res.data.total;
      });
    },
    // jumpApp
    jumpApp() {
      this.$router.push({
        path: "/dataServicePlatform/applyForApp",
      });
    },
    // 查看
    viewDetails(row) {
      console.log("查看", row.appCode);
      this.$router.push({
        path: "/dataServicePlatform/appDetail",
        query: {
          appCode: row.appCode,
        },
      });
    },
    // 编辑
    edit(row) {
      console.log("编辑", row.appCode);
      this.$router.push({
        path: "/dataServicePlatform/editApp",
        query: {
          appCode: row.appCode,
        },
      });
    },
    // 应用服务关系
    appService(row) {
      console.log("应用服务关系");
      this.diaDataProp = [
        {
          prop: "name",
          label: "名称",
          align: "center",
        },
        {
          prop: "description",
          label: "描述",
          align: "center",
        },
      ];
      this.appServiceVis = true;
      this.curAppCode = row.appCode;
      this.$api
        .qryAppService({
          appCode: this.curAppCode,
        })
        .then((res) => {
          console.log(res, 23333);
          this.diaDataList = res.data;
        });
    },
    // 弹窗tab
    handleClick(tab, event) {
      console.log(tab.name);
      if (tab.name === "first") {
        this.$api
          .qryAppService({
            appCode: this.curAppCode,
          })
          .then((res) => {
            this.diaDataList = res.data;
          });
      } else {
        this.$api
          .qryAppAccessService({
            appCode: this.curAppCode,
          })
          .then((res) => {
            this.diaDataList = res.data;
          });
      }
    },
    // 删除
    delApp(row) {
      console.log(row);
      this.message = "您要删除此应用吗？";
      this.confirmDialog = true;
      this.currentId = row.appCode;
    },
    // 确认删除
    confrim() {
      this.confirmDialog = false;
      this.$api
        .deleteApp({
          appCode: this.currentId,
        })
        .then((res) => {
          console.log(res, "删除");
          if (res.code == 0 || res.code == 200) {
            this.$message.success(res.msg);
            this.queryList();
          } else {
            this.$message.error(res.msg);
          }
        });
    },
    // 分页
    handleCurrentChange(val) {
      console.log(`当前页: ${val}`);
      this.currentPage = val;
      this.queryList();
    },
  },
};
