import request from "@/utils/request";

export default {
  name: "DocApply",
  data() {
    return {
      applyForm: {
        content: '',
        caliber: '',
        generationTime: '',
        destinationAddress: '',
        separator: '',
        fileFormat: '',
      },
      applyRules: {
        content: [
          {required: true, message: '请输入文件内容', trigger: 'blur'}
        ],
        caliber: [
          {required: true, message: '请输入口径', trigger: 'blur'}
        ],
        generationTime: [
          {required: true, message: '请输入生成时间', trigger: 'blur'}
        ],
        destinationAddress: [
          {required: true, message: '请输入目标地址', trigger: 'blur'}
        ],
        separator: [
          {required: true, message: '请输入分隔符', trigger: 'blur'}
        ],
        fileFormat: [
          {required: true, message: '请输入文件接口格式', trigger: 'blur'}
        ]
      }
    };

  },
  methods: {
    handleResetForm() {
      for (let key in this.applyForm) {
        this.applyForm[key] = ''
      }
    },
    handleSubmitApply() {
      this.$refs['applyFormRef'].validate(valid => {
        if (valid) {
          request({
            url: '/DataFile/fileInfo',
            method: 'post',
            data: {
              ...this.applyForm
            }
          }).then(res => {
            if (res.code === 0) {
              this.$message({
                message: res.msg,
                type: 'success',
              });
            } else {
              this.$message({
                message: res.msg,
                type: 'error'
              })
            }
          })

        } else {
          return false;
        }
      })

    },
  },
};
