import {
    mapGetters,
    mapState
} from "vuex";
import Avatar from "@eladmin/assets/images/avatar.png";
import RoleSelect from "@eladmin/components/RoleSelect";
import ThemeSelect from "@portal/components/ThemeSelect";
import Logo from "@portal/layout/components/Sidebar/Logo";

export default {
    name: "portalHome",
    components: {
        RoleSelect,
        ThemeSelect,
        Logo,
    },
    data() {
        return {
            Avatar: Avatar,
            logo: require('../../../../public/images/logo.png'),
            searchValue: "", // 搜索内容
            // title
            active: 1,
            titleList: [
                {
                    imgUrl: require("../../../assets/explore.png"),
                    title: '数据探索',
                    desc: '为了借助社交产品的流量,让用户主动分享APP中的内容到社交平...',
                    active: 1,
                    children: [
                        {
                            title: '数据资产平台',
                            path: '/',
                            desc: '下面我们会从不同维度来分析一下APP的社交分享功能设计，看看这里面有哪...'
                        },
                        {
                            title: '数据实验室',
                            path: '/laboratory/sqlAutoAnalysis',
                            desc: '下面我们会从不同维度来分析一下APP的社交分享功能设计，看看这里面有哪...'
                        },
                    ]
                },
                {
                    imgUrl: require("../../../assets/service.png"),
                    title: '数据服务',
                    desc: '为了借助社交产品的流量,让用户主动分享APP中的内容到社交平...',
                    active: 2,
                    children: [
                        {
                            title: '数据服务平台',
                            path: '/dataServicePlatform/myService',
                            desc: '下面我们会从不同维度来分析一下APP的社交分享功能设计，看看这里面有哪...'
                        },
                    ]
                },
                {
                    imgUrl: require("../../../assets/application.png"),
                    title: '数据应用',
                    desc: '为了借助社交产品的流量,让用户主动分享APP中的内容到社交平...',
                    active: 3,
                    children: [
                        {
                            title: '指标管理系统',
                            // path: '',
                            desc: '下面我们会从不同维度来分析一下APP的社交分享功能设计，看看这里面有哪...'
                        },
                        {
                            title: '标签管理系统',
                            // path: '',
                            desc: '下面我们会从不同维度来分析一下APP的社交分享功能设计，看看这里面有哪...'
                        },
                    ]
                },
                {
                    imgUrl: require("../../../assets/manage.png"),
                    title: '数据管理',
                    desc: '为了借助社交产品的流量,让用户主动分享APP中的内容到社交平...',
                    active: 4,
                    children: [
                        {
                            title: '敬请期待',
                            desc: '敬请期待...'
                        },
                    ]
                },
            ],
            menuList: [],
        };
    },
    mounted() {

    },
    watch: {

    },
    computed: {
        ...mapGetters(["sidebar", "device", "user", "baseApi"]),
        ...mapState({
            showBreadcrumb: (state) => state.settings.showBreadcrumb,
        }),
        showLogo() {
            return this.$store.state.settings.sidebarLogo;
        },
        show: {
            get() {
                return this.$store.state.settings.showSettings;
            },
            set(val) {
                this.$store.dispatch("settings/changeSetting", {
                    key: "showSettings",
                    value: val,
                });
            },
        },
    },
    created() {
        // 加载数据
        this.menuList = this.titleList[0].children
        console.log(this.menuList);
    },
    methods: {
        // 输入框
        inputChange(value) {
            this.searchValue = value;
            // localStorage.setItem("searchKeyWords", value);
        },
        globalSearch() {
            this.$router.push({
                path: "/searchPage/index",
                query: {
                    data: this.searchValue,
                    from: "home",
                },
            });
        },
        open() {
            this.$confirm("确定注销并退出系统吗？", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning",
            }).then(() => {
                this.logout();
            });
        },
        logout() {
            this.$store.dispatch("LogOut").then(() => {
                location.reload();
            });
        },
        // 鼠标移入
        titleEnter(item) {
            this.active = item.active
            this.menuList = item.children
        },
        // 打开新窗口
        openNewWindow(item) {
            if(item.path) {
                let routeData = this.$router.resolve({ path: item.path }); 
                window.open(routeData.href, '_blank');
            }
        }
    },
};
