$common-green: rgba(97, 189, 83, 1);
$common-grey: rgba(56, 81, 105, 1);
.recommendPage {
  width: 100%;
  height: 100%;
}
.recommendPage .swiper-container {
  position: relative;
  width: 100%;
}

.recommendPage .swiper-container .swiper-slide {
  width: 180px;
  border: 1px solid $common-green;
  color: #000;
  font-size: 16px;
  text-align: center;
  height: 70%;
  opacity: 0.5;
  border-radius: 5px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background: url("../../../assets/bgDep.png") 150% 50% no-repeat #fff;
  box-shadow: 0px 2px 4px rgba(130, 204, 118, 0.5);
}
.recommendPage .swiper-container .swiper-slide-prev {
  opacity: 0.7;
  height: 86%;
  width: 200px;
}

.recommendPage .swiper-container .swiper-slide-next {
  opacity: 0.7;
  height: 86%;
  width: 200px;
}
.recommendPage .swiper-container .swiper-slide-active {
  background: url("../../../assets/bgDep.png") 100% 50% no-repeat #61bd53;
  color: #fff !important;
  width: 240px !important;
  height: 99%;
  opacity: 1;

  .dept-item-top {
    color: #fff;
  }
  .dept-item-bottom {
    .p1 {
      color: #fff;
      font-size: 20px;
    }
    .p2 {
      font-size: 12px;
      color: #fff;
    }
  }
}
.dept-item-top {
  color: $common-green;
  font-size: 14px;
  padding-bottom: 10px;
  .user {
    background: url("../../../assets/depUser.png") 100% 100% no-repeat;
  }
}

.userIcon {
  width: 15px;
  height: 15px;
  position: relative;
  top: 2px;
  left: -4px;
}
.dept-item-bottom {
  .p1 {
    color: rgba(56, 81, 105, 1);
    font-size: 16px;
    font-weight: 600;
  }
  .p2 {
    font-size: 12px;
    color: rgba(56, 81, 105, 1);
  }
}
.swiper-button-prev,
.swiper-button-next {
  background-size: 15px 20px;
}
