ul,
li {
  list-style: none;
  margin: 0;
  padding: 0;
}
.portal-home {
  height: 100vh;
  background: #fff url("../../../assets/portalBannar.png") 0 0 no-repeat;
  background-size: 100% auto;
  overflow: hidden;
}
// header
.portal-header {
  width: 100vw;
  // min-width: 1366px;
  // height: 190px;
  display: flex;
  position: relative;
  align-items: center;
  // background: url("../../../assets/bannar.png") 0 0 no-repeat;
  // background-size: 100% 100%;

  .portal-header-title {
    // left: 155px;
    // top: 19px;
    width: 105px;
    height: 33px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: flex-start;
    // margin-top: 19px;
    margin-left: 24px;

    .portal-header-title-top {
      left: 0px;
      top: 0px;
      width: 96px;
      height: 22px;
      display: flex;
      color: #1f1a16;
      font-size: 16px;
    }

    .portal-header-title-bottom {
      left: 0px;
      top: 22px;
      width: 105px;
      height: 11px;
      display: flex;
      color: #1f1a16;
      font-size: 8px;
      white-space: nowrap;
      transform: scale(0.65);
      margin-left: -18px;
    }
  }
  .portal-header-search {
    width: 401px;
    height: 32px;
    display: flex;
    justify-content: space-between;
    // margin-top: 19px;
    margin-left: 5%;
    ::v-deep .el-input__inner {
      border: 1px solid #76c820;
    }
  }

  .el-input--mini .el-input__inner {
    height: 32px;
  }

  .el-input--mini .el-input__icon {
    height: 28px;
  }
  .portal-header-desc {
    position: absolute;
    top: 120px;
    left: 220px;
    p {
      width: 480px;
      font-size: 14px;
      line-height: 20px;
      margin: 0;
      color: #1a761f;
    }
  }

  .right-menu {
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    height: 70px;
    // position: absolute;
    margin-right: 24px;
    color: #1f1a16;
    font-size: 14px;

    .profile-picture {
      margin-right: 8px;
    }
  }
}

// container
.portal-container {
  width: 100vw;
  // min-width: 1366px;
  height: calc(100vh - 70px);
  display: flex;
  justify-content: center;
  overflow: auto;
  .portal-container-content {
    width: 85%;
    // max-width: 1000px;
    height: 100%;
    // margin: 0 auto;
    padding-top: 5%;
    padding-bottom: 5%;
    display: flex;
    flex-direction: column;
    // align-items: center;
    .portal-header-desc {
      height: 24%;
      // margin-bottom: 5%;
      p {
        width: 35%;
        height: 100%;
        font-size: calc(100vw * 16 / 1920);
        // line-height: 20px;
        line-height: 1.5;
        margin: 0;
        color: #1a761f;
        overflow: auto;
      }
    }
    // title
    .menu-title {
      width: 100%;
      height: 33%;
      margin: 25px 0 0 0;
      padding: 0;
      display: flex;
      justify-content: space-between;
      box-sizing: border-box;
      li {
        // width: 214px;
        width: 24%;
        height: 100%;
        padding: 0 20px;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        box-sizing: border-box;
        border-radius: 8px;
        img {
          width: 50%;
          // height: 40%;
        }
        span {
          font-size: 20px;
          color: #293138;
          color: #fff;
          // font-weight: 700;
          margin-top: 3%;
        }
        p {
          font-size: 12px;
          line-height: 18px;
          color: rgba(41, 49, 56, 0.7);
          margin: 12px 0 0 0;
        }
      }
      li:nth-child(1) {
        background: linear-gradient(
          180deg,
          rgba(97, 189, 83, 1) 0%,
          rgba(153, 212, 95, 1) 100%
        );
        backdrop-filter: blur(15px);
      }
      li:nth-child(2) {
        background: linear-gradient(
          180deg,
          rgba(114, 119, 237, 1) 0%,
          rgba(114, 165, 237, 1) 99.95%
        );
        backdrop-filter: blur(15px);
      }
      li:nth-child(3) {
        background: linear-gradient(
          180deg,
          rgba(78, 141, 230, 1) 0%,
          rgba(78, 177, 230, 1) 100%
        );
        backdrop-filter: blur(15px);
      }
      li:nth-child(4) {
        background: linear-gradient(
          180deg,
          rgba(227, 178, 54, 1) 0%,
          rgba(227, 201, 54, 1) 100%
        );
        backdrop-filter: blur(15px);
      }
      // li.active {
      //   background: #fff;
      //   border: 1px solid #76c820;
      //   border-bottom: 6px solid #76c820;
      //   border-radius: 8px;
      // }
    }
    // menu
    .menu-list {
      width: 100%;
      height: 33%;
      margin: 20px 0 0 0;
      padding: 13px 20px;
      border-radius: 8px;
      box-sizing: border-box;
      display: flex;
      flex-wrap: nowrap;
      align-items: center;
      background: rgba(127, 202, 116, 1);
      overflow-x: auto;
      position: relative;
      overflow: visible;
      .triangle {
        position: absolute;
        width: 0;
        height: 0;
        border-left: 15px solid transparent;
        border-right: 15px solid transparent;
        border-bottom: 15px solid rgba(97, 189, 83, 1);
        top: -15px;
        left: 12%;
      }
      .listBox {
        width: 100%;
        height: 100%;
        display: flex;
        flex-wrap: nowrap;
        align-items: center;
        overflow: auto;
      }
      li {
        width: 24%;
        height: 85%;
        margin: 0 9px;
        padding: 16px 23px;
        box-sizing: border-box;
        border-radius: 4px;
        background: #f6faf2;
        cursor: pointer;
        flex-shrink: 0;
        .title {
          // font-size: 14px;
          font-size: calc(100vw * 16 / 1920);
          color: #293138;
          display: flex;
          align-items: center;
          .box {
            width: 14px;
            height: 14px;
            border-radius: 2px;
            display: flex;
            justify-content: center;
            align-items: center;
            box-sizing: border-box;
            margin-right: 8px;
            background: #d5e3c9;

            i {
              width: 8px;
              height: 8px;
              border-radius: 2px;
              background: #79ac43;
            }
          }
          span {
            font-weight: 700;
          }
        }
        p {
          width: 100%;
          // height: 60px;
          // font-size: 12px;
          font-size: calc(100vw * 16 / 1920);
          line-height: 1.5;
          color: rgba(41, 49, 56, 0.7);
          display: -webkit-box;
          -webkit-box-orient: vertical;
          -webkit-line-clamp: 3;
          overflow: hidden;
        }
      }
    }
    .menu-list1 {
      background: linear-gradient(
        180deg,
        rgba(97, 189, 83, 1) 0%,
        rgba(153, 212, 95, 1) 100%
      );
      .triangle {
        border-bottom: 15px solid rgba(97, 189, 83, 1);
        left: 12%;
      }
    }
    .menu-list2 {
      background: linear-gradient(
        180deg,
        rgba(114, 119, 237, 1) 0%,
        rgba(114, 165, 237, 1) 99.95%
      );
      .triangle {
        border-bottom: 15px solid rgba(114, 119, 237, 1);
        left: 37%;
      }
    }
    .menu-list3 {
      background: linear-gradient(
        180deg,
        rgba(78, 141, 230, 1) 0%,
        rgba(78, 177, 230, 1) 100%
      );
      .triangle {
        border-bottom: 15px solid rgba(78, 141, 230, 1);
        left: 63%;
      }
    }
    .menu-list4 {
      background: linear-gradient(
        180deg,
        rgba(227, 178, 54, 1) 0%,
        rgba(227, 201, 54, 1) 100%
      );
      .triangle {
        border-bottom: 15px solid rgba(227, 178, 54, 1);
        left: 87%;
      }
    }
  }
  
}
// footer
.portal-footer {
  width: 100%;
  min-width: 1366px;
  height: 60px;
  background: url("../../../assets/footer.png") 0 0 no-repeat;
  background-size: 100% 100%;
  position: fixed;
  bottom: 0;
}
