result:
.operation {
    height: calc(100vh - 130px);
    // height: 100%;
    // margin: 16px;
    display: flex;
    .left {
        width: 260px;
        min-width: 260px;
        padding: 16px;
        background: #fff;
        border-radius: 8px;
        box-sizing: border-box;
        ::v-deep .el-tabs {
            height: auto;
            .el-tabs__header {
                margin: 0;
                .el-tabs__nav-wrap {
                    .el-tabs__nav-scroll {
                        .el-tabs__nav {
                            .el-tabs__item {
                                margin-right: 2px;
                            }
                        }
                    }
                }
                .el-tabs__nav-wrap::after {
                    background: transparent;
                }
            }
        }

        .tree {
            width: 100%;
            height: 100%;
            overflow: auto;
            margin-top: 16px;
            ::v-deep .el-input {
                height: 30px;
                .el-input__inner {
                    height: 100%;
                    line-height: 30px;
                    background: #f2f3f5;
                    border: none;
                }
            }
            .el-tree {
                margin-top: 8px;
                padding-top: 8px;
                height: calc(100% - 95px);
                border: none;
            }
        }
    }
    .content {
        flex: 1;
        margin-left: 16px;
        background: #fff;
        padding: 12px;
        border-radius: 8px;
        // 目录部分
        .catalogue {
            // height: 160px;
            // 一级 二级 四级
            .catalogueType {
                width: 100%;
                height: 20px;
                display: flex;
                align-items: center;
                font-size: 12px;
                color: rgba(32, 34, 36, 1);
                margin-bottom: 4px;
                span {
                    width: 80px;
                    margin-right: 8px;
                    line-height: 20px;
                }
                ::v-deep .el-tabs {
                    max-width: 70%;
                    overflow: auto;
                    height: auto;
                    .el-tabs__header {
                        margin: 0;
                        height: 20px;
                        .el-tabs__nav-wrap.is-scrollable {
                            padding: 0 40px 0 0;
                        }
                        .el-tabs__nav-wrap {
                            height: 100%;
                            // 左箭头
                            .el-tabs__nav-prev {
                                line-height: 20px;
                                right: 20px;
                                // color: #e5e5e5;
                                color: #202224;
                                font-size: 16px;
                                .el-icon-arrow-left {
                                    position: absolute;
                                    right: 0;
                                }
                                .el-icon-arrow-left::before {
                                    content: "";
                                }
                            }
                            // 右箭头
                            .el-tabs__nav-next {
                                line-height: 20px;
                                right: 0;
                                // color: #e5e5e5;
                                color: #202224;
                                font-size: 16px;
                                .el-icon-arrow-right::before {
                                    content: "";
                                }    
                            }
                            // 箭头禁用状态
                            .is-disabled {
                                color: #202224;
                            }
                            .el-tabs__nav-scroll {
                                height: 100%;
                                .el-tabs__nav {
                                    height: 100%;
                                    .el-tabs__active-bar {
                                        display: none;
                                    }
                                    .el-tabs__item {
                                        padding: 2px 6px;
                                        height: 100%;
                                        line-height: 16px;
                                        margin-right: 22px;
                                        font-size: 12px;
                                    }
                                    .el-tabs__item.is-active {
                                        background: #79AC43;
                                        color: #fff;
                                        border-radius: 2px;
                                    }
                                }
                            }
                        }
                        .el-tabs__nav-wrap::after {
                            display: none;
                        }
                    }
                }

            }
        }
        // 精准筛选
        .filterConditon {
            width: 100%;
            display: flex;
            align-items: center;
            font-size: 12px;
            color: rgba(32, 34, 36, 1);
            margin-bottom: 8px;
            .desc {
                width: 80px;
                margin-right: 8px;
                line-height: 20px;
                white-space: nowrap;
            }
            .filterItem {
                width: auto;
                // height: 30px;
                // flex: 1;
                display: flex;
                justify-content: flex-start;
                align-items: center;
            }
            .filter {
                display: flex;
                justify-content: flex-start;
                align-items: center;
                .el-input {
                    max-width: 160px;
                    font-size: 12px;
                }
                .el-select {
                    max-width: 160px;
                    font-size: 12px;
                }
                .el-cascader {
                    max-width: 120px;
                    font-size: 12px;
                }
            }
            span {
                margin-right: 8px;
            }
            .el-input {
                max-width: 180px;
                margin-right: 8px;
            }
            .el-select {
                max-width: 180px;
                margin-right: 8px;
                .el-input {
                    margin-right: 0;
                    .el-input__inner {
                        max-width: 180px;
                    }
                }
            }
            .el-cascader {
                max-width: 180px;
                margin-right: 8px;
                .el-input {
                    margin-right: 0;
                    .el-input__inner {
                        max-width: 180px;
                    }
                }
            }
            .el-button {
                margin-left: 8px;
                background: #79ac43;
                color: #fff;
                border: none;
            }
        }
        // 列表部分
        .list {
            display: flex;
            flex-wrap: wrap;
            // justify-content: space-between;
            align-content: flex-start;
            height: calc(100% - 149px);
            overflow: auto;
            .listItem {
                width: 32%;
                // height: 31%;
                height: 165px;
                margin-bottom: 8px;
                padding: 12px;
                border: 1px solid #e8e8e8;
                border-radius: 4px;
                margin-right: 1%;
                .title {
                    height: 20px;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    font-size: 14px;
                    color: #202224;
                    margin-bottom: 8px;
                    span {
                        .el-tag {
                            margin-right: 8px;
                            color: #409eff;
                            background-color: #ecf5ff;
                            border: 1px solid #d9ecff;
                        }
                        .el-tag.el-tag--success {
                            background-color: #f2f7ec;
                            border-color: #e4eed9;
                            color: #79ac43;
                        }
                        .el-tag.el-tag--warning {
                            background-color: #fff8e6;
                            border-color: #fff1cc;
                            color: #ffba00;
                        }
                    }
                    i {
                        cursor: pointer;
                    }
                    .el-icon-star-on {
                        color: #f7ba2a;
                        font-size: 18px;
                    }
                }
                .detail {
                    font-size: 12px;
                    p {
                        height: 20px;
                        line-height: 20px;
                        background: #fafafa;
                        margin: 0 0 4px 0;
                        width: 100%;

                        display: flex;
                        justify-content: space-between;
                        span {
                            width: 100%;
                            white-space: nowrap;
                            overflow: hidden;
                            text-overflow: ellipsis;
                            color: #808080;
                        }
                    }
                }
                .btn {
                    width: 100%;
                    height: 20px;
                    margin-top: 4px;
                    display: flex;
                    align-items: center;
                    div {
                        width: 49%;
                        text-align: center;
                    }
                    .line {
                        color: #e6ebf5;
                    }
                    .offline {
                        color: #f35957;
                    }
                }
            }
        }
        // 分页
        .pagination {
            margin-top: 8px;
            height: 32px;
            display: flex;

            flex-direction: row-reverse;
            .el-pagination {
                margin: 0;
            }
        }
    }
}
