export default {
  name: "processDetails",
  props: {
    tableId: {
      type: String,
      default: "",
    },
    detailVis: {
      type: Boolean,
      default: false,
    },
  },
  data() {
    return {
      // basicinfo
      basicinfo: {
        tableName: "ind_dim_info_dd_org",
        chineseName: "指标机构表",
        owner: "48",
        tenantCode: "idmp",
        project: "std_01_uat1",
        driverType: "odps",
        myCrtTime: "2022-01-01",
        myUpdTime: "2022-04-12",
        online: "是",
        catalogue1: "个贷",
        catalogue2: "网贷",
        catalogue1Name: "产品/贷款/个贷",
        catalogue2Name: "产品/贷款/网贷",
      },
      dataList: [],
      propList: [],
      id: "",
      assetsState: "",
      // 目录树
      catalogueList: [],
      cataValue: [],
    };
  },
  watch: {
    detailVis(val) {
      console.log(val, 23333333);
      if (val) {
        console.log(this.tableId, "详情弹窗");
        // 数据
        this.$api
          .getTableById({
            id: this.tableId,
            isRecord: false,
          })
          .then((res) => {
            let data = res.data;
            this.basicinfo = data.table;
            // 表格数据
            this.dataList = data.column;
            // 发布状态
            this.assetsState = this.basicinfo.assetsState;
            // 返回的目录  -- 渲染
            let temp = this.basicinfo.catalogues;
            this.cataValue = [];
            for (let key in temp) {
              key = key.split("/");
              this.cataValue.push({ id: key });
            }
          });
      }
    },
  },
  created() {
    // 目录树
    this.$api.getAllCatalogueList({}).then((res) => {
      this.catalogueList = res.data;
    });
    this.propList = [
      {
        prop: "columnName",
        label: "字段名称",
        align: "center",
      },
      {
        prop: "columnMessage",
        label: "字段信息",
        align: "center",
      },
      {
        prop: "type",
        label: "字段类型",
        align: "center",
      },
      {
        prop: "length",
        label: "字段长度",
        align: "center",
      },
      {
        prop: "isNull",
        label: "是否可以为空",
        align: "center",
      },
    ];
  },
  mounted() {},
  methods: {
    transOnline(online) {
      if (online === 1) {
        return "是";
      } else if (online === 0) {
        return "否";
      } else {
        return "";
      }
    },
    handleClose() {
      this.$emit("handleClose");
    },
  },
};
