import crudIndCategory from '@/api/indicators/indCategory'
import CRUD, {crud, form, header, presenter} from "@crud/crud";
const defaultCrud = CRUD({
  title: '指标信息',
  url: crudIndCategory.url,
  sort: 'id,desc',
  crudMethod: { ...crudIndCategory.crud }
})
const defaultForm = {
  parentName: null,
  categoryName: null,
  categoryType: null,
  id: null,
  parentId: null,
  prefix: null,
  operType: null
}
export default {
  name: 'IndCategory',
  // components: { pagination, crudOperation, rrOperation, udOperation },
   mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  data() {
    return {
      type: '0',
      parentId:  '0',
      dialogVisible: false,
      dialogTitle: '',
      filterText: '',
      activeName:'defaultCategory',
      defaultProps: { children: 'children', label: 'name' },
      treeData: [{ id: '0', categoryType: this.type, categoryName: '全部类别', children: [] ,parentName: null,parentId: null,prefix: null}],
      permission: {
        add: ['admin', 'indCategory:add'],
        edit: ['admin', 'indCategory:edit'],
        del: ['admin', 'indCategory:del']
      },
      rules: {
        id: [
          { required: true, message: '分类ID不能为空', trigger: 'blur' }
        ],
        categoryName: [
          { required: true, message: '分类名称不能为空', trigger: 'blur' }
        ]
      }
    }
  },
  watch: {
    filterText(val) {
      this.$refs.tree.filter(val)
    }
  },
  mounted() {
    // console.log('=====category---mounted....')
    this.init()
  },
  methods: {
    // 获取数据前设置好接口地址
    /*  [CRUD.HOOK.beforeRefresh]() {
        return true
    },*/
    filterNode(value, treeData) {
      if (!value) return true
      return treeData.categoryName.indexOf(value) !== -1
    },
    handleTabClick(tab, event) {
      console.log("tab===>, " + tab)
      this.type = tab.index;
      this.treeData[0].categoryType = tab.index;
      this.treeData[0].children = []
      this.init();
      this.handleNodeClick(this.treeData[0]);
    },
    // 点击事件
    handleNodeClick(data) {
      this.$emit('node-click', data)
    },
    handleAdd(data) {
      this.dialogVisible = true
      this.dialogTitle = "新增指标分类"
      this.form.categoryType = this.type
      //this.form.categoryName = data.categoryName
      this.form.parentName = data.categoryName
      this.form.parentId = data.id
      this.form.operType = "add"
      if(data.id > 0) {
        this.form.prefix = data.prefix
      }
    },
    handleEdit(data) {
      this.dialogVisible = true
      this.dialogTitle = "修改指标分类"
      this.form.categoryType = data.categoryType
      this.form.categoryName = data.categoryName
      this.form.parentName = data.parentName
      this.form.id = data.id
      this.form.operType = "edit"
      this.form.prefix = data.prefix
    },
    handleRemove(node, data) {
      const $this = this
      this.$confirm('确定要删除该节点及其所有子节点吗?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        crudIndCategory.crud.del([data.id]).then(res => {
          if (res.code !== 0) {
            $this.$message({
              type: 'error',
              message: '删除失败,' + res.msg
            })
          } else {
            $this.$message({
              type: 'success',
              message: '删除成功 '
            })
          }
          $this.init()

          // this.crud.notify(this.dict.label.user_status[val] + '成功', CRUD.NOTIFICATION_TYPE.SUCCESS)
        }).catch(() => {
          console.log('删除异常')
          $this.$message({
            type: 'error',
            message: '删除失败 '
          })
        })
      }).catch(() => {
      })
    },
    init() {
      crudIndCategory.tree(this.type, this.parentId).then(res => {
        console.log('======crudIndCategory.init======', res.toString())
        this.treeData[0].categoryType = this.type;
        this.treeData[0].children = res.data
        // this.crud.notify(this.dict.label.user_status[val] + '成功', CRUD.NOTIFICATION_TYPE.SUCCESS)
      }).catch(() => {
        console.log('')
        // data.enabled = !data.enabled
      })
    },
    cancel() {
      this.dialogVisible = false
    },
    submit() {
      if(this.form.operType === "add") {
        this.addCategory();
      } else {
        this.editCategory();
      }
    },
    addCategory() {
      const $this = this;
      if(this.form.categoryName == null){
        $this.$message({
          type:'error',
          message: '分类名称不能为空'
        })
        return
      }
      if(this.form.prefix == null){
        $this.$message({
          type:'error',
          message: '前缀不能为空'
        })
        return
      }
      crudIndCategory.crud.add({
        categoryName: this.form.categoryName,
        categoryType: this.form.categoryType,
        prefix: this.form.prefix != null? this.form.prefix.toUpperCase(): null,
        parentId: this.form.parentId
      }).then(res => {
        if(res.code === 0) {
          $this.$message({
            type: 'success',
            message: '添加成功 '
          })
          $this.init()
          this.form.categoryName = null
          this.form.categoryType  = null
          this.form.prefix = null
          this.form.parentId = null
          this.dialogVisible = false
        }else {
          $this.$message({
            type:'error',
            message: res.msg
          })
        }

      }).catch(() => {
        console.log('新增异常')
        $this.$message({
          type: 'error',
          message: '添加失败 '
        })
      })
    },
    editCategory() {
      const $this = this
      if(this.form.categoryName == null){
        $this.$message({
          type:'error',
          message: '分类名称不能为空'
        })
        return
      }
      if(this.form.prefix == null){
        $this.$message({
          type:'error',
          message: '前缀不能为空'
        })
        return
      }
      crudIndCategory.crud.edit({
        categoryName: this.form.categoryName,
        categoryType: this.form.categoryType,
        prefix: this.form.prefix != null? this.form.prefix.toUpperCase(): null,
        id: this.form.id
      }).then(res => {
        if(res.code === 0) {
          $this.$message({
            type: 'success',
            message: '修改成功 '
          })
          $this.init()
          this.form.categoryName = null
          this.form.categoryType  = null
          this.form.prefix = null
          this.form.parentId = null
          this.dialogVisible = false
        }else {
          $this.$message({
            type:'error',
            message: res.msg
          })
        }
      }).catch(() => {
        $this.$message({
          type: 'error',
          message: '修改失败 '
        })
      })
      this.$prompt('输入节点名称', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        inputValue: data.categoryName
        // inputPattern: /[\w!#$%&'*+/=?^_`{|}~-]+(?:\.[\w!#$%&'*+/=?^_`{|}~-]+)*@(?:[\w](?:[\w-]*[\w])?\.)+[\w](?:[\w-]*[\w])?/,
        // inputErrorMessage: '邮箱格式不正确'
      }).then(() => {

      }).catch(() => {
        this.$message({
          type: 'info',
          message: '修改失败'
        })
      })
    }
  }
}
