.portal-container {
  height: 100vh;
  width: 100vw;
  background-image: linear-gradient(#265cbc, #032b71);

  .portal-header {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;

    .portal-header-icon {
      height: 100px;
      width: 600px;
    }

    .portal-header-userinfo {
      color: white;
      display: flex;
      flex-direction: row;
      align-items: center;
      padding-right: 30px;

      .portal-header-userinfo-name {
        font-weight: bold;
      }
    }
  }

  .portal-main {
    margin: 150px 0 150px 0;

    .portal-main-menu {
      display: flex;
      justify-content: center;
      height: 100%;
      margin-left: 10px;
      margin-right: 10px;
    }

  }

  .portal-sub {
    .portal-sub-menu {
      height: 100%;
      display: flex;
      justify-content: space-between;
      margin-left: 100px;
      margin-right: 100px;

      .sub-menu-item {
        display: flex;
        flex-direction: column;
        align-items: center;
      }
    }

  }
}
