export default {
    data() {
        return {
            activeName: 'all',
            tableData: [],
            currentPage: 1,
            pageSize: 10,
            total: 50,
            save: false,
            // 数据预览
            dataPreVis: false,
            preDataList: [],
            preDataProp: [],
            // 申请权限
            authorityVis: false,
            // 加入购物车参数
            request: {
                assetId: '',
                beginTime: '',
                endTime: '',
            },
            // 直接申请
            directApplyVis: false,  
            daterange: '',
            radio: '2',
            // 提示弹窗
            tipDialog: false,
        }
    },
    created() {
        this.$api.qryAll({}).then(res=>{
            this.tableData = res.data
        })
        this.preDataProp = [
            {
                prop: 'A1',
                label: 'A1',
                align: 'center',
            },
            {
                prop: 'A2',
                label: 'A2',
                align: 'center',
            },
            {
                prop: 'A3',
                label: 'A3',
                align: 'center',
            },
            {
                prop: 'A4',
                label: 'A4',
                align: 'center',
            },
            {
                prop: 'A5',
                label: 'A5',
                align: 'center',
            },
            {
                prop: 'A6',
                label: 'A6',
                align: 'center',
            },
            {
                prop: 'A7',
                label: 'A7',
                align: 'center',
            },
        ]
        this.preDataList = [
            {
                A1: 'XXXXXXXXX',
                A2: 'XXXXXXXXX',
                A3: 'XXXXXXXXX',
                A4: 'XXXXXXXXX',
                A5: 'XXXXXXXXX',
                A6: 'XXXXXXXXX',
                A7: 'XXXXXXXXX',
            },
            {
                A1: 'XXXXXXXXX',
                A2: 'XXXXXXXXX',
                A3: 'XXXXXXXXX',
                A4: 'XXXXXXXXX',
                A5: 'XXXXXXXXX',
                A6: 'XXXXXXXXX',
                A7: 'XXXXXXXXX',
            },
            {
                A1: 'XXXXXXXXX',
                A2: 'XXXXXXXXX',
                A3: 'XXXXXXXXX',
                A4: 'XXXXXXXXX',
                A5: 'XXXXXXXXX',
                A6: 'XXXXXXXXX',
                A7: 'XXXXXXXXX',
            },
            {
                A1: 'XXXXXXXXX',
                A2: 'XXXXXXXXX',
                A3: 'XXXXXXXXX',
                A4: 'XXXXXXXXX',
                A5: 'XXXXXXXXX',
                A6: 'XXXXXXXXX',
                A7: 'XXXXXXXXX',
            },
            {
                A1: 'XXXXXXXXX',
                A2: 'XXXXXXXXX',
                A3: 'XXXXXXXXX',
                A4: 'XXXXXXXXX',
                A5: 'XXXXXXXXX',
                A6: 'XXXXXXXXX',
                A7: 'XXXXXXXXX',
            },
        ]
    },
    methods: {
        // tab切换
        handleClick(tab,event) {
            console.log(tab.name);
            if(tab.name==='all') {
                this.$api.qryAll({}).then(res=>{
                    this.tableData = res.data
                })
            } else if (tab.name==='table') {
                this.$api.qryTable({}).then(res=>{
                    this.tableData = res.data
                })
            } else if (tab.name==='API') {
                this.$api.qryAPI({}).then(res=>{
                    this.tableData = res.data
                })
            } else if (tab.name==='target') {
                this.$api.qryTarget({}).then(res=>{
                    this.tableData = res.data
                })
            } else if (tab.name==='label') {
                this.$api.qryLabel({}).then(res=>{
                    this.tableData = res.data
                })
            }
        },
        // 分页
        handleCurrentChange(val) {
            console.log(`当前页: ${val}`);
        },
        // 详情查看
        viewDetails(type,item) {
            this.$api.viewShopCarDetail({
                assetId: item.assetId
            }).then(res => {
                console.log(res,type);
                this.$router.push({
                    path: '/detailsView/'+type,
                    query: {
                        res: res
                    }
                })
            })
        },
        // 数据预览
        previewData(item) {
            this.dataPreVis = true
        },
        // 申请权限
        applyForAuthority(item) {
            this.authorityVis = true
            // this.request.assetId = item.assetId
        },
        // 加入购物车
        addShopCar() {
            this.$api.addShopCar(this.request).then(res => {
                console.log(res);
                this.authorityVis = false
            })
        },
        // 直接申请
        directApply() {
            this.authorityVis = false
            this.directApplyVis = true 
        },
        // 确定申请
        confirmApply() {
            // 关闭申请弹窗
            this.directApplyVis = false 
            // 直接申请权限接口 -- 当前为审批接口  待修正
            this.$api.initiateApproval({}).then(res => {
                console.log(res);
                this.tipDialog = true
            }).then(() => {
                // 提示框展示三秒自动关闭
                setTimeout(()=>{
                    this.tipDialog = false
                },3000)
            })
        }
    }
}
