export default {
    data() {
        return {
            // basicinfo
            basicinfo: {
                id: '测试指标',
                targetName: '绿色贷款余额',
                targetType: '组合指标',
                classifyOne: '业务开展',
                classifyTwo: '公司业务拓展规模',
                businessDept: '公司银行部',
                targetLevel: '总行级',
                targetDefinition: '截至统计时点，我行发放的符合环保部、人民银行、银监会三部门联合发文定义的绿色贷款余额。',
                targetRule: '绿色贷款包括用途为绿色农业、林业开发、工业节能节水环保、自然保护。',
                currency: '人民币/外币折人民币',
                myCrtTime: '2022-01-01',
                myUpdTime: '2022-04-12',
                dimension1: '贷款产品类型',
                dimensionValue1: '绿色贷款',
                dimension2: '贷款产品类型',
                dimensionValue2: '绿色贷款',
                catalogue1: '个贷',
                catalogue2: '网贷',
                catalogue1Name: '产品/贷款/个贷',
                catalogue2Name: '产品/贷款/网贷',
                statisticsRate: '月',
                superviseAlert: 'xxxx',
                measureUnit: 'xxxx',
                dataType: 'xxxx',
                referenceBasis: 'xxxx',
                processSystem: 'xxxx',
                safetyLevel: 'xxxx',
                valueAttribute: 'xxxx',
            },
            // 
            radio1: '1',
            radio2: '',
            radio3: '1',
            radio4: '',
            radio5: '',
            radio6: '1',
            radio7: '',
            radio8: '',
            radio9: '',
            radio10: '',
            detailsView: false,
            id: '',
            assetsState: '',
            // 目录树
            catalogueList: [],
            cataValue: []
        }
    },
    created() {
        if(this.$route.name === '指标详情') {
            this.detailsView = true
        }
        // 数据
        this.basicinfo = this.$route.query.res
        // id
        this.id = this.basicinfo.id

        // 发布状态
        this.assetsState = this.basicinfo.assetsState
        // 返回的目录  -- 渲染
        let temp = this.basicinfo.catalogues
        for (let key in temp) {
            key = key.split('/')
            this.cataValue.push({id:key})
        }
        console.log(this.$route.query.res,333333);
    },
    methods: {
        confirmModify() {
            console.log('确定修改');
            let list = JSON.parse(JSON.stringify(this.cataValue))
            list = list.map(item => {
                return item = item.id.join('/')
            });
            this.$api.updateAssetTarget({
                id: this.id,
                list: list,
                assetsState: this.assetsState
            }).then(res => {
                this.$message.success('修改成功!')
            })
        },
        handleChange(value) {
            console.log(value);
        },
        deleteCatalogue(index) {
            this.cataValue.splice(index,1)
        },
        addCatalogue() {
            if(this.cataValue.length>4) {
                this.$message.warning('最多添加5条目录')
            } else {
                this.cataValue.push({
                    id: []
                })
            }
        },
    }
}
