export default {
    name: 'MyShopCar',
    data() {
        return {
            searchInput: '',
            // 表格
            tableData: [],
            propList: [],
            // 分页
            pageSize: 5,
            currentPage: 1,
            total: 4,
            // 审批 弹窗
            confirmDialog: false,
            // 提示弹窗
            tipDialog: false,
            // 审批参数
            businessKey: '',
            // 当前选中行
            selectRows: [],
        }
    },
    created() {
        this.propList = [
            {
                prop: 'tableName',
                label: '表名',
                width: '180',
            },
            {
                prop: 'tableComment',
                label: '表描述信息',
            },
            {
                prop: 'project',
                label: '存储项目名称',
                width: '180',
            },
            {
                prop: 'driverType',
                label: '底层存储类型',
            },
            {
                prop: 'owner',
                label: '所属用户',
            },
            
        ]
    },
    mounted() {
        this.queryList()
    },
    methods: {
        // 查询列表
        queryList() {
            this.$api.qryShopCar({
                pageNo: String(this.currentPage),
                pageSize: String(this.pageSize),
                tableName: this.searchInput
            }).then(res => {
                console.log(res.data);
                let data = res.data
                this.tableData = data.records
                this.total = data.total
            })
        },
        // 复选框选中
        handleSelectionChange(val) {
            // [{}]  [{},{}]
            console.log('当前选中',val);
            this.selectRows = val
            if(this.selectRows.length<1) {
                this.businessKey = ''
            } else {
                this.selectRows.forEach(ele => {
                    this.businessKey = this.businessKey += ele.assetId + ','
                });
                this.businessKey = this.businessKey.slice(0,this.businessKey.length-1)
            }
        },
        // 详情查看
        viewDetails(row) {
            console.log('详情查看',row.assetId);
            this.$api.viewShopCarDetail({
                assetId: row.assetId
            }).then(res => {
                console.log(res);
                this.$router.push({
                    path: '/detailsView/table',
                    query: {
                        res: res.data
                    }
                })
            })
        },
        // 发起审批  -- 单个
        initiateApproval(row) {
            console.log('发起审批',row.assetId);
            this.confirmDialog = true
            this.businessKey = row.assetId
        },
        // 发起审批  -- 批量
        batchOperation() {
            this.confirmDialog = true
        },
        // 确认审批
        confrim() {
            this.confirmDialog = false
            // 调接口审批
            console.log(this.businessKey,'审批参数');
            this.$api.initiateApproval({
                businessKey: this.businessKey
            }).then(res => {
                console.log(res);
                this.tipDialog = true
            }).then(() => {
                this.queryList()
                // 提示框展示三秒自动关闭
                setTimeout(()=>{
                    this.tipDialog = false
                },3000)
            })

        },
        // 删除
        deleteRow(row) {
            console.log('删除',row.id);
            this.$api.delShopCar({
                Id: row.id
            }).then(res => {
                this.queryList()
                this.$message.success(res.message)
            })
        },
        // 分页
        handleSizeChange(val) {
            console.log(`每页 ${val} 条`);
        },
        handleCurrentChange(val) {
            this.currentPage = val
            this.queryList()
            console.log(`当前页: ${val}`);
        }, 
    },

}
