import { mixinsFunctions } from "@/mixins/mixinsFunc";
export default {
  mixins: [mixinsFunctions],
  data() {
    return {
      // basicinfo
      basicinfo: {
        tableName: "",
        chineseName: "",
        owner: "",
        tenantCode: "",
        project: "",
        driverType: "",
        myCrtTime: "",
        myUpdTime: "",
        online: "是",
        catalogue1: "",
        catalogue2: "",
        catalogue1Name: "",
        catalogue2Name: "",
      },
      basicInfoProp: [
        {
          prop: "tableName",
          label: "表名",
          align: "center",
        },
        {
          prop: "owner",
          label: "所属用户",
          align: "center",
        },
        {
          prop: "deptName",
          label: "业务属主部门",
          align: "center"
        },
        {
          prop: "tenantCode",
          label: "租户编码",
          align: "center",
        },
        {
          prop: "project",
          label: "存储项目名称",
          align: "center",
        },
        {
          prop: "driverType",
          label: "底层存储类型",
          align: "center",
        },
        {
          prop: "createTimeStr",
          label: "创建时间",
          align: "center",
        },
        {
          prop: "updateTimeStr",
          label: "更新时间",
          align: "center",
        },
        {
          prop: "online",
          label: "是否公开",
          align: "center",
        },
      ],
      basicInfoList: [],
      dataList: [],
      propList: [],
      detailsView: false,
      id: "",
      assetsState: "",
      // 目录树
      catalogueList: [],
      cataValue: [],
      tableBgImg: require("../../../assets/tableBg.png"),
    };
  },
  methods: {
    initData() {
      if (this.$route.name === "表详情") {
        this.detailsView = true;
      }
      // 目录树
      this.$api.getAllCatalogueList({}).then((res) => {
        this.catalogueList = res.data;
      });
      // 数据
      this.id = this.$route.query.id; // 通过路由传递id
      this.$api
        .getTableById({
          id: this.id,
          isRecord: this.detailsView,
        })
        .then((res) => {
          this.basicInfoList = [];
          this.cataValue = [];
          let data = res.data;
          this.basicinfo = data.table;
          this.basicInfoList.push(this.basicinfo);
          // 表格数据
          this.dataList = data.column;
          // 发布状态
          this.assetsState = this.basicinfo.assetsState;
          // 返回的目录  -- 渲染
          let temp = this.basicinfo.catalogues;

          for (let key in temp) {
            key = key.split("/");
            this.cataValue.push({ id: key });
          }
          console.log(this.cataValue);
        });
      this.propList = [
        {
          prop: "columnName",
          label: "字段名称",
          align: "center",
        },
        {
          prop: "columnMessage",
          label: "字段信息",
          align: "center",
        },
        {
          prop: "type",
          label: "字段类型",
          align: "center",
        },
        {
          prop: "length",
          label: "字段长度",
          align: "center",
        },
        {
          prop: "isNull",
          label: "是否可以为空",
          align: "center",
        },
      ];
    },
    confirmModify() {
      let list = JSON.parse(JSON.stringify(this.cataValue));
      list = list.map((item) => {
        return (item = item.id.join("/"));
      });

      let listSet = Array.from(new Set(list));
      if (listSet.length !== list.length) {
        this.$message.error("添加目录不能重复");
      } else {
        this.$api
          .updateAssetTable({
            id: this.id,
            list: list,
            assetsState: this.assetsState,
          })
          .then((res) => {
            if (res.code == 0) {
              this.$message.success("修改成功!");
              this.closeThisPage();
            } else {
              this.$message.error(res.msg);
              this.closeThisPage();
            }
          });
      }
    },
    handleChange(value) {
      console.log(value);
    },
    deleteCatalogue(index) {
      this.cataValue.splice(index, 1);
    },
    transOnline(online) {
      if (online === 1) {
        return "是";
      } else if (online === 0) {
        return "否";
      } else {
        return "";
      }
    },
    addCatalogue() {
      if (this.cataValue.length > 4) {
        this.$message.warning("最多添加5条目录");
      } else {
        this.cataValue.push({
          id: [],
        });
      }
    },
  },
};
