import { mixinsFunctions } from "@/mixins/mixinsFunc";
export default {
  mixins: [mixinsFunctions],
  data() {
    return {
      // basicinfo
      basicinfo: {
        labelName: "白领",
        labelCode: "A001",
        classifyCode: "B001",
        classifyName: "B001",
        stateCode: "状态码",
        fromCode: "来源码",
        invalidTime: "2022-12-31",
        description:
          "对一类事物共性谢谢谢谢谢寻寻寻寻寻寻寻寻寻寻寻寻寻寻寻寻寻寻寻寻的描述",
        businessCaliber: "口径描述",
        catalogue1: "个贷",
        catalogue2: "网贷",
        catalogue1Name: "产品/贷款/个贷",
        catalogue2Name: "产品/贷款/网贷",
      },
      detailsView: false,
      id: "",
      assetsState: "",
      // 目录树
      catalogueList: [],
      cataValue: [],
    };
  },
  computed: {},
  methods: {
    initData() {
      if (this.$route.name === "标签详情") {
        this.detailsView = true;
      }
      // 目录树
      this.$api.getAllCatalogueList({}).then((res) => {
        this.catalogueList = res;
      });
      // 数据
      this.id = this.$route.query.id; // 通过路由传递id
      this.$api
        .getLabelById({
          id: this.id,
          isRecord: this.detailsView,
        })
        .then((res) => {
          this.basicinfo = res;
          this.cataValue= [];
          // 发布状态
          this.assetsState = this.basicinfo.assetsState;
          // 返回的目录  -- 渲染
          let temp = this.basicinfo.catalogues;
          for (let key in temp) {
            key = key.split("/");
            this.cataValue.push({ id: key });
          }
        });
    },
    confirmModify() {
      let list = JSON.parse(JSON.stringify(this.cataValue));
      list = list.map((item) => {
        return (item = item.id.join("/"));
      });
      let listSet = Array.from(new Set(list));
      if (listSet.length !== list.length) {
        this.$message.error("添加目录不能重复");
      } else {
        this.$api
          .updateAssetLabel({
            id: this.id,
            list: list,
            assetsState: this.assetsState,
          })
          .then((res) => {
            this.$message.success("修改成功!");
            this.closeThisPage();
          });
      }
    },
    handleChange(value) {
      console.log(value);
    },
    deleteCatalogue(index) {
      this.cataValue.splice(index, 1);
    },
    addCatalogue() {
      if (this.cataValue.length > 4) {
        this.$message.warning("最多添加5条目录");
      } else {
        this.cataValue.push({
          id: [],
        });
      }
    },
    // 失效时间转换
    transInvalidTime(time) {
      let invalidTime = time ? (time.indexOf("9999") > -1 ? "永久" : time) : "";
      return invalidTime;
    },
  },
};
