result:
ul,
li {
    list-style: none;
    margin: 0;
    padding: 0;
}

// header
.portal-header {
    width: 100%;
    min-width: 1366px;
    height: 310px;
    display: flex;
    position: relative;
    background: url("../../../assets/bannar.png") 0 0 no-repeat;
    background-size: 100% 100%;

    .portal-header-title {
        left: 155px;
        top: 19px;
        width: 105px;
        height: 33px;
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        align-items: flex-start;
        margin-top: 19px;
        margin-left: 24px;

        .portal-header-title-top {
            left: 0px;
            top: 0px;
            width: 96px;
            height: 22px;
            display: flex;
            color: rgba(255, 255, 255, 1);
            font-size: 16px;
        }

        .portal-header-title-bottom {
            left: 0px;
            top: 22px;
            width: 105px;
            height: 11px;
            display: flex;
            color: rgba(255, 255, 255, 1);
            font-size: 8px;
            white-space: nowrap;
            transform: scale(0.65);
            margin-left: -18px;
        }
    }
    .portal-header-desc {
        position: absolute;
        top: 135px;
        left: 220px;
        h1 {
            font-size: 26px;
            color: #fff;
            margin: 0 0 22px 0;
        }
        p {
            width: 440px;
            font-size: 14px;
            line-height: 20px;
            color: #fff;
            margin: 0;
        }
    }

    .right-menu {
        display: flex;
        align-items: center;
        height: 70px;
        position: absolute;
        right: 24px;
        color: #fff;
        font-size: 14px;

        .profile-picture {
            width: 30px;
            height: 30px;
            border-radius: 30px;
            margin-right: 8px;
        }
    }
}

// container
.portal-container {
    width: 100%;
    min-width: 1366px;
    height: calc(100vh - 400px);
    display: flex;
    justify-content: flex-start;
    overflow: auto;
    .portal-container-content {
        width: 100%;
        max-width: 1000px;
        height: 100%;
        margin: 0 auto;
        .portal-header-search {
            width: 401px;
            height: 32px;
            display: flex;
            justify-content: space-between;
            margin: 30px auto;
        }

        .el-input--mini .el-input__inner {
            height: 32px;
        }

        .el-input--mini .el-input__icon {
            height: 28px;
        }
        // title
        .menu-title {
            width: 100%;
            height: 230px;
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: space-between;
            box-sizing: border-box;
            li {
                width: 215px;
                height: 100%;
                padding: 12px 20px 0 20px;
                display: flex;
                flex-direction: column;
                justify-content: center;
                align-items: center;
                box-sizing: border-box;
                span {
                    font-size: 20px;
                    color: #293138;
                    font-weight: 700;
                    margin-top: 12px;
                }
                p {
                    font-size: 12px;
                    line-height: 18px;
                    color: rgba(41, 49, 56, 0.7);
                    margin: 12px 0 0 0;
                }
            }
            li.active {
                background: #fff;
                border: 1px solid #76c820;
                border-bottom: 6px solid #76c820;
                border-radius: 8px;
            }
        }
        // menu
        .menu-list {
            width: 100%;
            height: 294px;
            margin: 20px 0 0 0;
            padding: 13px 20px;
            border: 1px solid #76c820;
            border-radius: 8px;
            box-sizing: border-box;
            display: flex;
            flex-wrap: wrap;
            // justify-content: space-between;
            // align-items: center;
            background: #fff;
            li {
                width: 220px;
                height: 120px;
                margin: 0 9px;
                padding: 16px 23px;
                box-sizing: border-box;
                border-radius: 4px;
                background: #f6faf2;
                cursor: pointer;
                .title {
                    font-size: 14px;
                    color: #293138;
                    display: flex;
                    align-items: center;
                    .box {
                        width: 14px;
                        height: 14px;
                        border-radius: 2px;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        box-sizing: border-box;
                        margin-right: 8px;
                        background: #d5e3c9;

                        i {
                            width: 8px;
                            height: 8px;
                            border-radius: 2px;
                            background: #79ac43;
                        }
                    }
                    span {
                        font-weight: 700;
                    }
                }
                p {
                    width: 100%;
                    height: 60px;
                    font-size: 12px;
                    line-height: 20px;
                    color: rgba(41, 49, 56, 0.7);
                    display: -webkit-box;
                    -webkit-box-orient: vertical;
                    -webkit-line-clamp: 3;
                    overflow: hidden;
                }
            }
        }
    }
}
// footer
.portal-footer {
    width: 100%;
    min-width: 1366px;
    height: 60px;
    background: url("../../../assets/footer.png") 0 0 no-repeat;
    background-size: 100% 100%;
    position: fixed;
    bottom: 0;
}
