import { instances } from '../../api/workflow/activiti'
import FlowImage from '../../components/Activiti/FlowImage'

export default {
  name: 'Instance',
  data() {
    return {
      rows: [],
      loading: false,
      current: {},
      showImage: false,
      page: {
        // 页码
        start: 0,
        // 每页数据条数
        size: 10,
        // 总数据条数
        total: 0
      }
    }
  },
  components: { FlowImage },
  mounted() {
    this.refresh()
  },
  methods: {
    refresh() {
      instances({
          order: 'desc',
          latest: true,
          sort: 'startTime',
          size: this.page.size,
          start: this.page.start
        }
      ).then(res => {
        //console.log(res)
        this.rows = res.data.data
        this.page.total = res.data.total
      })
    },
    viewImage(row) {
      this.current.processInstanceId = row.id
      this.current.processDefinitionId = row.processDefinitionId
      this.showImage = true
    },
    // 当前页改变
    pageChangeHandler(e) {
      this.page.start = e
      this.refresh()
    },
    // 每页条数改变
    sizeChangeHandler(e) {
      this.page.size = e
      this.page.start = 1
      this.refresh()
    }
  }
}
