import { definitions, startTestFlow } from '../../api/workflow/activiti'
import FlowImage from '../../components/Activiti/FlowImage'

export default {
  name: 'Definition',
  data() {
    return {
      rows: [],
      loading: false,
      startFlow: false,
      showImage: false,
      form: {
        processId: '',
        businessKey: '',
        parameter: ''
      },
      page: {
        // 页码
        start: 0,
        // 每页数据条数
        size: 10,
        // 总数据条数
        total: 0
      },
      rules: {
        businessKey: [
          { required: true, message: '业务主键不能为空', trigger: 'blur' }
        ]
      }
    }
  },
  components: { FlowImage },
  mounted() {
    this.refresh()
  },
  methods: {
    refresh() {
      definitions({
          order: 'asc',
          latest: true,
          sort: 'id',
          size: this.page.size,
          start: this.page.start
        }
      ).then(res => {
        //console.log(res)
        this.rows = res.data.data
        this.page.total = res.data.total
      })
    },
    viewImage(row) {
      this.form.processId = row.key
      this.showImage = true
    },
    toStartFlow(row) {
      this.startFlow = true
      this.form.processId = row.key
    },
    doStartFlow() {
      let parameter = {}
      if (this.form.parameter) {
        parameter = JSON.parse(this.form.parameter)
      }
      startTestFlow(this.form.processId, this.form.businessKey, parameter).then(res => {
        this.$message.success('流程发起成功:' + res.data)
        this.startFlow = false
      })
    },
    // 当前页改变
    pageChangeHandler(e) {
      this.page.start = e
      this.refresh()
    },
    // 每页条数改变
    sizeChangeHandler(e) {
      this.page.size = e
      this.page.start = 1
      this.refresh()
    }
  }
}
