function getDay(num) {
  var today = new Date();
  var nowTime = today.getTime();
  var ms = 24 * 3600 * 1000 * num;
  today.setTime(parseInt(nowTime + ms));
  today.setHours(23);
  today.setMinutes(59);
  today.setSeconds(59);

  /* var oYear = today.getFullYear();
   var oMoth = (today.getMonth() + 1).toString();
