export default {
  inject: ['reload'],
  name: 'ThemeSelect',
  data() {
    return {
      currentTheme: '',
      data: config,
      cstyle: () => require(`../../layout/styles/${this.getStyle()['style']}`)
    }
  },
  components: {},
  computed: {
    currentStyle() {
      return this.getStyle()
