import {
    todoCount,
    subscribeRpt
} from '@/api/indicators/dashboard'

import CountTo from 'vue-count-to'
export default {
    components: {
        CountTo
    },
    data() {
        return {
            activeName: 'second',
            count: {
                todo: '',
                newIp: 0,
                newVisits: 0,
                recentIp: 0,
                recentVisits: 0
            },
            subscribe: {
                title: '我的订阅',
                total: ''
            },
            searchInput: '',
            // 历史记录
            historyTags: Array.from(JSON.parse(localStorage.getItem('historyTags'))) || [],

        }
    },
    mounted() {
        todoCount().then(res => {
            if (res.code === '0') {
                this.count.todo = res.data.total
            }
        })
        subscribeRpt().then(res => {
            this.subscribe.total = res.data.total
        })
    },
    methods: {
        openTodoList() {
            this.$router.push({
                path: '/indicator/waitApprove'
            })
        },
        openFavorite() {
            this.$router.push({
                path: '/indicator/resultSubscribe/index'
            })
        },
        openTable() {
            this.$router.push({
                path: '/indicator/noticeQuery'
            })
        },
        /*openIndex() {
            this.$router.push({path: '/indicators/query'})
        },*/
        openSubscribeList() {
            this.$router.push({
                path: '/indicator/resultSubscribe'
            })
        },
        // 搜索
        queryList(inputValue) {
            console.log(111);
            this.historyTags.push(inputValue);
            console.log(this,historyTags,2333);
            let tags = Array.from(new Set(this.historyTags))
            console.log(this,historyTags,3444);
            localStorage.setItem('historyTags',JSON.stringify(tags))
            this.searchInput = ''
        },
        // 单个标签删除
        handleClose(tag) {
            this.historyTags.splice(this.historyTags.indexOf(tag), 1);
            let tags = Array.from(new Set(this.historyTags))
            localStorage.setItem('historyTags',JSON.stringify(tags))
        },
        // 全部删除
        clearAllHistory() {
            this.historyTags = []
            let tags = Array.from(new Set(this.historyTags))
            localStorage.setItem('historyTags',JSON.stringify(tags))
        }
    }
}
