import {getListByKeywords,getDetail} from '../../../api/searchPage/es'
    export default {
        name: 'SearchPage',
        components: {},
        data() {
            return {
                searchInput: '',
                assetType:'',
                activeType: 'all',
                filterList: [],
                filterTable: [
                    {value: 1,label: '目录层级1'},
                    {value: 2,label: '目录层级2'},
                    {value: 3,label: '目录层级3'},
                    {value: 4,label: '所属系统'},
                    {value: 5,label: '存储项目名称'},
                ],
                filterAPI: [
                    {value: 1,label: '归属应用'},
                    {value: 2,label: '目录层级1'},
                    {value: 3,label: '目录层级2'},
                    {value: 4,label: '目录层级3'},
                    {value: 5,label: '底层存储类型'},
                ],
                filterTarget: [
                    {value: 1,label: '指标一级分类'},
                    {value: 2,label: '指标二级分类'},
                    {value: 3,label: '指标类型'},
                    {value: 4,label: '属主部门'},
                ],
                filterTag: [
                    {value: 1,label: '标签所属内部机构'},
                    {value: 2,label: '标签分类名称'},
                    {value: 3,label: '标签适用对象'},
                    {value: 4,label: '标签来源'},
                ],
                tableData: [],
                allList: [],
                tableList: [],
                targetList: [],
                labelList: [],
                apiList: []
            }
        },
        watch: {
            activeType(val) {
                if(val==='all') {

                } else if(val==='table') {
                    this.filterList = this.filterTable
                } else if(val==='API') {
                    this.filterList = this.filterAPI
                } else if(val==='target') {
                    this.filterList = this.filterTarget
                } else if(val==='tag') {
                    this.filterList = this.filterTag
                }
            }
        },
        created() {

        },
        mounted() {},
        computed: {},
        methods: {
            //查询列表
            queryList(val) {
                const parm ={
                    "keywords":val,
                    "page":1,
                    "size":10,
                    "assetType":'0'
                }
                this.assetType ='0'
                getListByKeywords(parm).then(data =>{
                    this.tableData = data.obj,
                    this.allList = data.obj,
                    this.tableList = data.tableList,
                    this.targetList = data.targetList,
                    this.labelList = data.labelList,
                    this.apiList = data.apiList
                    console.log("obj"+JSON.stringify(data.obj))
                    console.log("obj"+JSON.stringify(data))
                    console.log("count"+JSON.stringify(data.count))
                })
            },
            //查看详情
            toDetail(item){
                console.log("============="+item.assetType)
                const parm ={
                    "item":item
                }
                getDetail(parm).then(data =>{

                })

                },
            handleClick(tab,event) {
                console.log("====="+tab.name)
                if(tab.name == 'all'){
                    this.tableData = this.allList
                }else if (tab.name == 'table'){
                    this.tableData = this.tableList
                }else if (tab.name == 'target'){
                    this.tableData = this.targetList
                }else if(tab.name == 'tag'){
                    this.tableData = this.labelList
                }else if(tab.name == 'API'){
                    this.tableData = this.apiList
                }
                console.log(this.assetType);
            }
        }
    }
