.applyForDoc {
    height: calc(100vh - 145px);
    padding: 16px;
    margin: 16px;
    background: #fff;
    border-radius: 8px;
    .contain {
    height: 100%;
    overflow: auto;
    .title {
        font-size: 16px;
        color:#79ac43;
        line-height: 22px;
        display: flex;
        align-items: center;
        i {
            width: 12px;
            height: 12px;
            border-radius: 2px;
            background: #79ac43;
            margin-right: 8px;
        }
    }
    // 基本信息
    .info {
        .content {
            margin-top: 8px;
            padding: 16px;
            background: #fafafa;
            font-size: 14px;
            color: #202224;
            .item {
                display: flex;
                flex-direction: column;
                margin-bottom: 16px;
                span {
                    line-height: 20px;
                    margin-bottom: 8px;
                }
                .reqParams {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    margin-bottom: 8px;
                    .params {
                        width: 100%;
                        margin-right: 1%;
                    }
                    .el-button {
                        width: 20px;
                        height: 20px;
                        border-radius: 4px;
                        padding: 4px;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        margin-right: 1%;
                    }
                }
                .addParams {
                    width: 100%;
                    height: 30px;
                    border: 1px dashed #ccc;
                    margin-top: 8px;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    cursor: pointer;
                    span {
                        margin: 0 0 0 8px;
                    }
                }
            }
            .itemSpecial {
                display: flex;
                justify-content: space-between;
                p {
                    width: 30%;
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;
                    .el-date-editor {
                        width: 100%;
                    }
                    span {
                        line-height: 20px;
                        margin-bottom: 8px;
                    }
                }
            }
        }
    }
    .btn {
        margin-top: 24px;
        display: flex;
        justify-content: flex-end;
        .el-button {
            width: 86px;
            height: 30px;
            margin-left: 14px;
            background: #79ac43;
            color: #fff;
            border: none;
            border-radius: 4px;
        }
        .resetBtn {
            background: #fff;
            border: 1px solid #79ac43;
            color: #79ac43;
        }
    }
}
    
}
