export default {
    name: 'Classify',
    data() {
        return {
            fileList: []
        }
    },
    methods: {
        // 移出前
        beforeRemove(file, fileList) {
            return this.$confirm(`确定移除 ${file.name}？`)
        },
        // 移除附件
        upLoadRemove(file, fileList) {
            let tempFileList = []
            for (var index = 0; index < this.fileList.length; index++) {
                if (this.fileList[index].name !== file.name) {
                    tempFileList.push(this.fileList[index])
                }
        }
        this.fileList = tempFileList
        },
        // 监控上传文件列表
        uploadChange(file, fileList) {
            let existFile = fileList.slice(0, fileList.length - 1).find(f => f.name === file.name);
            if (existFile) {
                this.$message.error('当前文件已经存在!');
                fileList.pop();
            }
            this.fileList = fileList;
        },
        // 上传到服务器
        submitUpload() {
            let formData = new FormData()
            this.fileList.forEach(item => {
                formData.append('files', item.raw)
            })
            console.log(formData,2333);
            this.$api.importCcn({file: formData}).then((res) => {
                console.log(res);
                // 消息提示
                this.$message({
                    message: '文件上传成功',
                    type: 'success',
                    duration: 1500
                })
            })
        },
    }
}
