export default {
    name: 'MyShopCar',
    data() {
        return {
            searchInput: '',
            // 表格
            tableData: [],
            propList: [],
            // 分页
            pageSize: 9,
            currentPage: 1,
            total: 4,
            // 审批 弹窗
            confirmDialog: false,
            // 提示弹窗
            tipDialog: false,
            // 审批参数
            request: [],
            // 当前选中行
            selectRows: [],
            // 是否批量操作
            isbatch: false,
        }
    },
    created() {
        this.propList = [
            {
                prop: 'tableName',
                label: '表名',
                width: '180',
            },
            {
                prop: 'tableComment',
                label: '表描述信息',
            },
            {
                prop: 'project',
                label: '存储项目名称',
                width: '180',
            },
            {
                prop: 'driverType',
                label: '底层存储类型',
            },
            {
                prop: 'owner',
                label: '所属用户',
            },
            {
                prop: 'crtDate',
                label: '申请时间'
            },
            
        ]
    },
    mounted() {
        this.queryList()
    },
    methods: {
        // 查询列表
        queryList() {
            this.$api.qryShopCar({
                pageNo: String(this.currentPage),
                pageSize: String(this.pageSize),
                tableName: this.searchInput
            }).then(res => {
                console.log(res.data);
                let data = res.data
                this.tableData = data.records
                this.total = data.total
            })
        },
        // 复选框选中
        handleSelectionChange(val) {
            // [{}]  [{},{}]
            // this.selectRows = val
            // if(this.selectRows.length<1) {
            //     this.businessKey = ''
            // } else {
            //     this.selectRows.forEach(ele => {
            //         this.businessKey = this.businessKey += ele.tableId + ','
            //     });
            //     this.businessKey = this.businessKey.slice(0,this.businessKey.length-1)
            // }
            this.selectRows = []
            val.forEach( item =>{
                this.selectRows.push({tableId:item.tableId,beginTime:item.beginTime,endTime:item.endTime})
            })
        },
        // 详情查看
        viewDetails(row) {
            console.log('详情查看',row.tableId);
            this.$router.push({
                path: '/detailsView/table',
                query: {
                    id: row.tableId
                }
            })
        },
        // 发起审批  -- 单个
        initiateApproval(row) {
            this.confirmDialog = true
            this.isbatch = false
            this.request = [
                {
                    tableId: row.tableId,
                    beginTime: row.beginTime,
                    endTime: row.endTime
                }
            ]
        },
        // 发起审批  -- 批量
        batchOperation() {
            this.confirmDialog = true
            this.isbatch = true
        },
        // 确认审批
        confrim() {
            this.confirmDialog = false
            let list = []
            if(this.isbatch) {
                list = this.selectRows
            }else {
                list = this.request
            }
            // 调接口审批
            console.log(list,'审批参数');
            this.$api.initiateApproval({
                list: list
            }).then(res => {
                console.log(res);
                this.tipDialog = true
            }).then(() => {
                this.queryList()
                // 提示框展示三秒自动关闭
                setTimeout(()=>{
                    this.tipDialog = false
                },3000)
            })

        },
        // 删除
        deleteRow(row) {
            console.log('删除',row.id);
            this.$api.delShopCar({
                Id: row.id
            }).then(res => {
                this.queryList()
                this.$message.success(res.message)
            })
        },
        // 分页
        handleSizeChange(val) {
            console.log(`每页 ${val} 条`);
        },
        handleCurrentChange(val) {
            this.currentPage = val
            this.queryList()
            console.log(`当前页: ${val}`);
        }, 
    },

}
