import request from "@/utils/request";

export default {
  name: "UmRelate",
  data() {
    return {
      params: {
        dataworksAccount: this.$route.query.dataworksAccount,
        projectName: this.$route.query.projectName
      },
      queryForm: {
        //nickName: "",
        total: 0,
        page: 1,
        size: 10,
        loading: false
      },
      tableData: [],
      addDialogVisible: false,
      editDialogVisible: false,
      editForm: {
        dataworksAccount : '',
        projectName : '',
        nickName: ''
      },
      oldDataAccount: "",
      oldProjectName: "",
      editFormRules: {
        nickName: [
          {required: true, trigger: ['blur', 'change'], message: '请输入姓名'}
        ]
      }
    };
  },
  methods:{
    // 分页
    handleSizeChange(val) {
      this.queryForm.size = val;
      this.queryPageList();
    },
    handleCurrentChange(val) {
      this.queryForm.page = val;
      this.queryPageList();
    },
    async queryPageList() {
      this.queryForm.loading = true
      let res = await request({
        url: '/datalab/dataworksAcProject/detailRelateList',
        method: 'post',
        data: {
          pageSize: this.queryForm.size,
          pageNum: this.queryForm.page,
          dataworksAccount: this.params.dataworksAccount,
          projectName: this.params.projectName,
          nickName: this.queryForm.nickName
        }
      })
      if (res.data && res.data.records) {
        this.tableData = res.data.records
        this.queryForm.total = res.data.total
        this.queryForm.page = res.data.current

      }
      this.queryForm.loading = false
      //console.log(res)
    },
    //重置查询条件
    resetQueryForm() {
      this.queryForm.nickName = ''
      this.queryPageList()
    },
    handleCloseDetail(){
      this.addDialogVisible = false
      this.editDialogVisible = false
      this.$refs.editFormRef.resetFields()
      this.queryPageList()
    },
    //新增
    handleAddRule() {
      this.addDialogVisible = true
      this.editForm = {}
    },
    //编辑
    handleEdit(index, row) {
      this.editDialogVisible = true
      this.editForm = row
      this.editForm.oldId = row.id
    },
    //提交新增
    handleSendAdd() {
      this.$refs['editFormRef'].validate(valid => {
        if (valid) {
          request({
            url: '/datalab/dataworksAcProject/addRelate',
            method: 'post',
            data: {
              dataworksAccount: this.params.dataworksAccount,
              projectName: this.params.projectName,
              id: this.editForm.id,
            }
          }).then(res => {
            if (res.returnCode === '200') {
              this.$message({
                message: '新增成功',
                type: 'success'
              })
            } else {
              this.$message({
                message: '新增失败,' + res.returnMsg,
                type: 'error'
              })
            }
            this.addDialogVisible = false
          })
        } else {
          return false
        }
      })
    },
    //提交编辑
    handleSendEdit() {
      this.$refs['editFormRef'].validate(valid => {
        if (valid) {
          request({
            url: '/datalab/dataworksAcProject/updateRelate',
            method: 'post',
            data: {
              dataworksAccount: this.params.dataworksAccount,
              projectName: this.params.projectName,
              id: this.editForm.id,
              oldId: this.editForm.oldId,
            }
          }).then(res => {
            if (res.returnCode === '200') {
              this.$message({
                message: '修改成功',
                type: 'success'
              })
            } else {
              this.$message({
                message: '修改失败,' + res.returnMsg,
                type: 'error'
              })
            }
            this.editDialogVisible = false
          })
        } else {
          return false
        }
      })
    },
    //删除
    handleDelete(index, row) {
      this.$confirm('确定删除UM用户关联吗?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        request({
          url: '/datalab/dataworksAcProject/deleteRelate',
          method: 'post',
          data: {
            dataworksAccount: row.dataworksAccount,
            id: row.id
          }
        }).then(res => {
          if (res.returnCode === '200') {
            this.$message({
              type: 'success',
              message: '删除成功!'
            });
            this.queryPageList()
          } else {
            this.$message({
              type: 'error',
              message: res.returnMsg
            });
          }
        })
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        });
      });
    },
    //查询用户
    async searchUsersAsync(queryString, callback) {
      let res = await request({
        url: '/datalab/dataworksAcProject/searchUsers',
        method: 'post',
        data: {
          queryString : queryString
        }
      })
      if (res.returnCode === '200') {
        for (let i of res.data) {
          i.value = i.nickName
        }
        callback(res.data)
      }
    },
    //处理选中的姓名
    handleSelectUser(item) {
      //console.log('item====' + item.id)
      this.editForm.id = item.id
    },
    blurForBug(){
      document.activeElement.blur()
    }
  },
  mounted() {
    this.queryPageList()
  },
  created() {
    //console.log('created:' + this.$route.query.dataworksAccount)
  }
}
