export default {
    data() {
        return {
            title: '文件申请',
            editPage: true,
            id: '',
            object: {
                name: '',
                description: '',
                overtime: '',
                currentLimit: '',
                type: '',
                destinationAddress: '',
                latestGenerationTime: '',
                separator: '',
                fileFormat: '',
                serviceType: '2',
                status: '',
                dept: '',            
                belongUser: '',            
                fieldList: '',            
                belongApplication: '',            
                belongApplicationName: '',            
                sitPath: '',            
                prePath: '',            
                serviceParamList: [
                    {
                        name: '',
                        type: '',
                        parameterType: '', // 1入参 2出参
                        description: '',
                        defaultValue: '',
                        isPass: '',
                    }
                ],  
                applicationInfoList: [],          
            },
            // 部门树
            deptTree: []
        }
    },
    created() { 
        this.editPage = this.$route.name === '文件编辑' ? true : false
        if(this.editPage) {
            this.title = '文件编辑'
        }
        this.$api.findDeptTree({}).then(res => {
            console.log(res);
            this.deptTree = res.data
        })
    },
    mounted() {
        if(this.$route.name === '文件编辑' || this.$route.name === '文件详情') {
            this.id = this.$route.query.id ? this.$route.query.id : ''
            this.$api.viewServiceDetail({
                id: this.id
            }).then(res => {
                console.log(res);
                this.object = res.data
            })

        }
    },
    methods: {
        // 重置
        reset() {
            console.log('重置');
            this.object = {
                serviceType: '2',
            }
        },
        // 提交申请
        submitApplication() {
            console.log('提交申请',this.object);
            this.$api.applyForService(this.object).then(res=>{
                console.log(res);
                this.$message.success('申请成功!')
            })
        },
        // 确认更新
        confirmModify() {
            console.log('确定修改',this.object);
            this.$api.serviceUpdate(this.object).then(res=>{
                console.log(res);
                this.$message.success('更新成功!')
            })
        }
    }
}
