export default {
    components: {},
    data() {
        return {
            searchInput: '',
            // 历史记录
            historyTags: Array.from(JSON.parse(localStorage.getItem('historyTags'))) || [],
            dateValue: '',
        }
    },
    mounted() {
        this.drawChart()
        // 轮播图
        let liWidth = $("#carousel ul>li").outerWidth();
        let count = 0;
        /////////
        $("#carousel ul").append($("#carousel ul").find("li").eq(0).clone())
        $("#carousel ul").append($("#carousel ul").find("li").eq(1).clone())
        $("#carousel ul").append($("#carousel ul").find("li").eq(2).clone())
        $("#carousel ul").append($("#carousel ul").find("li").eq(3).clone())
        $("#carousel ul").append($("#carousel ul").find("li").eq(4).clone())
        //////////
        $("#carousel ul").css("width", $("#carousel ul>li").length + "00%");
        let flag = true;
        $(".car .arrow-left").click(function () {
            if (flag) {
                flag = false;
                if (count == 0) {
                    count = $("#carousel ul>li").length - 5;
                    $("#carousel ul").css("left", -liWidth * count + "px");
                }
                count--;
                $("#carousel ul").animate({ "left": -liWidth * count }, function () {
                    return flag = true;
                });
            }
        })
        $(".car .arrow-right").click(function () {
            if (flag) {
                flag = false;
                if (count == $("#carousel ul>li").length - 5) {
                    count = 0;
                    $("#carousel ul").css("left", -liWidth * count + "px");
                }
                count++;
                $("#carousel ul").animate({ "left": -liWidth * count }, function () {
                    return flag = true;
                });
            }
        });
    },
    methods: {
        openSearchPage(desc) {
            this.$router.push({
                path: '/searchPage/index',
                query: {
                    desc: desc
                }
            })
        },
        // 搜索
        queryList(inputValue) {
            // 去除空格
            inputValue = inputValue.replace(/\s*/g,"")  

            this.historyTags.unshift(inputValue);
            this.historyTags = Array.from(new Set(this.historyTags))
            localStorage.setItem('historyTags',JSON.stringify(this.historyTags))
            this.searchInput = ''
            // this.$router.push({
            //     path: '/searchPage/index',
            // })
        },
        // 单个标签删除
        handleClose(tag) {
            this.historyTags.splice(this.historyTags.indexOf(tag), 1);
            localStorage.setItem('historyTags',JSON.stringify(this.historyTags))
        },
        // 全部删除
        clearAllHistory() {
            this.historyTags = []
            localStorage.setItem('historyTags',JSON.stringify(this.historyTags))
        },
        drawChart() {
            // 饼图
            let pieChartDom = document.getElementById('pieChart');
            let pieChart = this.$echarts.init(pieChartDom);
            let pieChartOption = {
                title: {
                    text: '资产分类',
                    left: '5%',
                    top: '5%'
                },
                tooltip: {
                    trigger: 'item'
                },
                legend: {
                    orient: 'vertical',
                    left: '60%',  //图例距离左的距离
                    y: 'center',  //图例上下居中
                    formatter: function (name) {
                        let data = pieChartOption.series[0].data
                        // console.log(data, 'data')
                        let total = 0
                        let tarValue
                        for (let i = 0; i < data.length; i++) {
                        total += data[i].value
                        if (data[i].name == name) {
                            tarValue = data[i].value
                        }
                        }
                        let v = tarValue
                        return `${name}     ${v}    `
                    },
                },
                series: [
                    {
                    name: 'Access From',
                    type: 'pie',
                    center: ['35%', '50%'],
                    radius: ['30%', '40%'],
                    avoidLabelOverlap: false,
                    label: {
                        show: false,
                        position: 'center'
                    },
                    emphasis: {
                        label: {
                        show: true,
                        fontSize: '20',
                        fontWeight: 'bold'
                        }
                    },
                    labelLine: {
                        show: false
                    },
                    data: [
                        { value: 1048, name: 'Search Engine' },
                        { value: 735, name: 'Direct' },
                        { value: 580, name: 'Email' },
                        { value: 484, name: 'Union Ads' },
                        { value: 300, name: 'Video Ads' }
                    ]
                    }
                ]
            }

            pieChartOption && pieChart.setOption(pieChartOption);
            // 柱状图
            let barChartDom = document.getElementById('barChart');
            let barChart = this.$echarts.init(barChartDom);
            let barChartOption = {
                // title: {
                //     text: ''
                // },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                    type: 'shadow'
                    }
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    // bottom: '3%',
                    top: '5%',
                    containLabel: true
                },
                xAxis: {
                    type: 'value',
                    boundaryGap: [0, 0.01]
                },
                yAxis: {
                    type: 'category',
                    data: ['1', '2', '3', '4', '5', '6']
                },
                series: [
                    {
                        name: '2011',
                        type: 'bar',
                        data: [1823, 2348, 2904, 10470, 13144, 63020],
                        itemStyle: {
                            normal: {
                                color: 'blue'
                            }
                        }
                    },
                ]
            }

            barChartOption && barChart.setOption(barChartOption);
            // 折线图
            let lineChartDom = document.getElementById('lineChart');
            let lineChart = this.$echarts.init(lineChartDom);
            let lineChartOption = {
                title: {
                    text: '业务趋势',
                    left: '5%',
                    top: '5%'
                },
                tooltip: {
                    trigger: 'axis'
                },
                legend: {
                    data: ['表资产', 'API资产', '目标资产', '标签资产'],
                    top: '5%'
                },
                grid: {
                    left: '5%',
                    right: '5%',
                    bottom: '5%',
                    containLabel: true
                },
                xAxis: {
                    type: 'category',
                    boundaryGap: false,
                    data: ['1', '2', '3', '4', '5', '6', '7']
                },
                yAxis: {
                    type: 'value'
                },
                series: [
                    {
                        name: '表资产',
                        type: 'line',
                        stack: 'Total',
                        data: [120, 132, 101, 134, 90, 230, 210]
                    },
                    {
                        name: 'API资产',
                        type: 'line',
                        stack: 'Total',
                        data: [220, 182, 191, 234, 290, 330, 310]
                    },
                    {
                        name: '目标资产',
                        type: 'line',
                        stack: 'Total',
                        data: [140, 152, 121, 114, 95, 220, 260]
                    },
                    {
                        name: '标签资产',
                        type: 'line',
                        stack: 'Total',
                        data: [210, 122, 141, 234, 250, 320, 340]
                    },
                ]
            }

            lineChartOption && lineChart.setOption(lineChartOption);


            // 折线图2
            let smoothLineChartDom = document.getElementById('smoothLineChart');
            let smoothLineChart = this.$echarts.init(smoothLineChartDom);
            let smoothLineChartOption = {
                title: {
                    text: '资产数据增长情况',
                    left: '5%',
                    top: '5%'
                },
                tooltip: {
                    trigger: 'axis'
                },
                legend: {
                    data: ['1部', '2部', '3部', '4部'],
                    top: '5%'
                },
                grid: {
                    left: '5%',
                    right: '5%',
                    bottom: '5%',
                    containLabel: true
                },
                xAxis: {
                    type: 'category',
                    boundaryGap: false,
                    data: ['1', '2', '3', '4', '5', '6', '7']
                },
                yAxis: {
                    type: 'value'
                },
                series: [
                    {
                        name: '1部',
                        type: 'line',
                        smooth: true,
                        stack: 'Total',
                        data: [120, 132, 101, 134, 90, 230, 210]
                    },
                    {
                        name: '2部',
                        type: 'line',
                        smooth: true,
                        stack: 'Total',
                        data: [220, 182, 191, 234, 290, 330, 310]
                    },
                    {
                        name: '3部',
                        type: 'line',
                        smooth: true,
                        stack: 'Total',
                        data: [140, 152, 121, 114, 95, 220, 260]
                    },
                    {
                        name: '4部',
                        type: 'line',
                        smooth: true,
                        stack: 'Total',
                        data: [210, 122, 141, 234, 250, 320, 340]
                    },
                ]
            }

            smoothLineChartOption && smoothLineChart.setOption(smoothLineChartOption);
            setTimeout(function () {
                window.onresize = function () {
                    pieChart.resize();
                    barChart.resize();
                    lineChart.resize();
                    smoothLineChart.resize();
                }
            }, 200)
        },
    }
}
