import crudIndSupplementaryReview, {toApprove} from '../../../api/indicators/indSupplementaryReview'
import {findIndByApproveId} from '../../../api/indicators/indIndicatorManual'
import CRUD, {presenter, header, form, crud} from '@crud/crud'
import rrOperation from '@crud/RR.operation'
import crudOperation from '@crud/CRUD.operation'
import udOperation from '@crud/UD.operation'
import pagination from '@crud/Pagination'

// crud交由presenter持有
const defaultCrud = CRUD({
  title: '补录审核',
  url: crudIndSupplementaryReview.url,
  sort: 'id,desc',
  crudMethod: {...crudIndSupplementaryReview.crud}
})
const defaultForm = {
  id: null,
  supplementaryDescribe: null,
  approveStatus: null,
  supplementaryId: null,
  infoId: null,
  crtUserCode: null,
  crtOrgCode: null,
  crtDate: null,
  updUserCode: null,
  updOrgCode: null,
  inputdate: null,
  updDate: null
}
const detailData = []
export default {
  name: 'IndSupplementaryReview',
  components: {pagination, crudOperation, rrOperation, udOperation},
  mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  dicts: ['RETENTION', 'IE_TYPE', 'IE_STATUS', 'IND_CURRENCY', 'IND_FIRST_TYPE', 'BUSSINESS_DEPT', 'IE_FREQUENCY', 'IND_APPROVE_STATUS', 'BE_APPROVE_STATUS'],
  data() {
    return {
      approvedDialog: false,
      DetailDialog: false,
      loading: true,
      ids: null,
      infoIds: null,
      detailData,
      permission: {
        add: ['admin', 'indSupplementaryReview:add'],
        edit: ['admin', 'indSupplementaryReview:edit'],
        del: ['admin', 'indSupplementaryReview:del']
      },
      options: [{
        value: '2',
        label: '同意'
      }, {
        value: '3',
        label: '驳回'
      }],
      rules: {
        id: [
          {required: true, message: 'ID不能为空', trigger: 'blur'}
        ]
      }
    }
  },
  created() {
    this.resetQuery()
  },
  methods: {
    resetQuery() {
      this.crud.resetQuery(false);
      this.crud.refresh()
    },
    // 获取数据前设置好接口地址
    [CRUD.HOOK.beforeRefresh]() {
      this.crud.optShow.del = false
      this.crud.optShow.add = false
      return true
    },
    Approved(data) {
      console.log(this.form.approveStatus);
      if (Object.keys(data).length == 0) {
        this.$message({
          message: '请先选择一条记录',
          type: 'warning'
        });
      } else if (data[0].approveStatus != '1') {
        this.$message({
          message: '请选择一条未审批过的记录',
          type: 'warning'
        });
      } else {
        this.approvedDialog = true,
            this.ids = data[0].id
        this.infoIds = data[0].infoId
      }
    },
    closeApprovedDialog() {
      this.approvedDialog = false
      this.form.approveStatus = ""
      this.ids = ""
      this.infoIds = ""
    },
    closeDetailDialog() {
      this.DetailDialog = false
      this.loading = true

    },
    showDetail(data) {
      this.DetailDialog = true
      var approveId = data[0].id
      findIndByApproveId(approveId).then(data => {
        console.log(data)
        this.detailData = data
        this.loading = false
      })
    },
    submit() {
      toApprove(this.ids, this.infoIds, this.form.approveStatus).then(data => {
        if (data.msg == "成功" || data.msg == "操作成功") {
          this.$message.success({
            message: '操作成功!'
          })
          this.closeApprovedDialog();
          this.crud.toQuery();
        } else {
          this.$message.error({
            message: data.msg
          })
        }
      })
    }
  }
}
