ul,
li {
    list-style: none;
}
.role {
    height: 95%;
    // 筛选条件
    .conditions {
        display: flex;
        .el-input {
            width: 180px;
            margin-right: 8px;
        }
        .el-select,.el-cascader {
            margin-right: 8px;
        }
        .el-button {
            margin-left: 8px;
        }
    }
    // 表格
    .table {
        margin-top: 16px;
        height: calc(100% - 125px);
        .el-table::before {
            background-color: #fff;
            height: 0;
        }
        ::v-deep .el-table {
            height: 90%;
            overflow: auto;
            margin-top: 24px;
            padding-left: 4px;
            // header
            .el-table__header-wrapper {
                .el-table__header {
                    tr {
                        th {
                            color: rgba(32, 34, 36, 1);
                            font-size: 14px;
                            font-weight: 400;
                            border: none;
                        }
                    }
                }
            }
            // body
            .el-table__body-wrapper {
                .el-table__body {
                    border-spacing: 0 8px;
                    tr {
                        td:first-child {
                            border-left: 1px solid rgba(232, 232, 232, 1);
                        }
                        td:last-child {
                            position: relative;
                            right: 4px;
                            border-right: 1px solid rgba(232, 232, 232, 1);
                        }
                        td {
                            color: rgba(77, 78, 80, 1);
                            border-top: 1px solid rgba(232, 232, 232, 1);
                            border-bottom: 1px solid rgba(232, 232, 232, 1);

                        }
                    }
                }
            }
            // 复选框
            .cell {
                white-space: nowrap;
                overflow: hidden;
                text-overflow: ellipsis;
                .el-checkbox {
                    width: 16px;
                    height: 16px;
                    .el-checkbox__input {
                        .el-checkbox__inner {
                            width: 16px;
                            height: 16px;
                        }
                    }
                    .el-checkbox__input.is-checked,.el-checkbox__input.is-indeterminate {
                        width: 16px;
                        height: 16px;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        background: #fff;
                        border: 1px solid #79AC43;
                        border-radius: 2px;
                    }
                    .el-checkbox__input.is-indeterminate {
                        .el-checkbox__inner {
                            width: 8px;
                            height: 4px;
                            background: #79AC43;
                        }
                        .el-checkbox__inner::before {
                            display: none;
                        }
                    }
                    .el-checkbox__input.is-checked {
                        .el-checkbox__inner {
                            width: 8px;
                            height: 8px;
                            background: #79AC43;
                        }
                        .el-checkbox__inner::after {
                            display: none;
                        }
                    }
                }
            }
        }
    }
    // 分页
    .pagination {
        position: absolute;
        bottom: 20px;
        right: 40px;
        height: 64px;
        display: flex;
        align-items: center;
        flex-direction: row-reverse;
        .el-pagination {
            margin: 0;
        }
    }
    // 新增弹窗
    ::v-deep .el-dialog__wrapper.addRoleDia {
        .el-dialog {
            border-radius: 8px;
            .el-dialog__body {
                padding: 10px 20px;
                .el-form {
                    width: 85%;
                    .el-form-item {
                        .el-form-item__content {
                            .el-select {
                                width: 100%;
                            }
                        }
                    }
                }
            }
            .el-dialog__footer {
                padding: 20px 20px 10px;
            }
        }
    }
    // 批量删除提示弹窗
    ::v-deep .el-dialog__wrapper.batchDeleteDia {
        .el-dialog {
            border-radius: 8px;
            padding: 16px;
            box-sizing: border-box;
            .el-dialog__header {
                padding: 0;
            }
            .el-dialog__body {
                line-height: 22px;
                padding: 20px 10px;
                font-size: 14px;
                display: flex;
                align-items: center;
                i {
                    color: #e6a23c;
                    font-size: 16px;
                    margin-right: 8px;
                }
                span {
                    color: rgba(0, 0, 0, 0.9);
                }
            }
            .el-dialog__footer {
                width: 100%;
                display: flex;
                justify-content: flex-end;
                padding: 0;
                margin-top: 8px;
                .el-button {
                    width: 84px;
                    height: 30px;
                    margin: 0;
                    border-radius: 4px;
                }
                .el-button--default {
                    border: none;
                    background: #e5e5e5;
                    color: #202224;
                }
                .el-button--primary {
                    background: #79ac43;
                }
            }
        }
    }
}
