import request from "@/utils/request";

export default {
  name: "DataworksAcProject",
  data() {
    return {
      queryForm: {
        dataworksAccount: "",
        total: 0,
        page: 1,
        size: 10,
        loading: false
      },
      tableData: [],
      addDialogVisible: false,
      editDialogVisible: false,
      editForm: {},
      oldDataAccount: "",
      oldProjectName: "",
      editFormRules: {
        dataworksAccount: [
          {required: true, trigger: ['blur', 'change'], message: '请输入dataworks账户'}
        ],
        projectName: [
          {required: true, trigger: ['blur', 'change'], message: '请输入ODPS Project'}
        ]
      }
    };
  },
  methods:{
    // 分页
    handleSizeChange(val) {
      this.queryForm.size = val;
      this.queryPageList();
    },
    handleCurrentChange(val) {
      this.queryForm.page = val;
      this.queryPageList();
    },
    async queryPageList() {
      this.queryForm.loading = true
      let res = await request({
        url: '/datalab/dataworksAcProject/list',
        method: 'post',
        data: {
          pageSize: this.queryForm.size,
          pageNum: this.queryForm.page,
          dataworksAccount: this.queryForm.dataworksAccount
        }
      })
      if (res.data && res.data.records) {
        this.tableData = res.data.records
        this.queryForm.total = res.data.total
        this.queryForm.page = res.data.pages

      }
      this.queryForm.loading = false
      //console.log(res)
    },
    //重置查询条件
    resetQueryForm() {
      this.queryForm.dataworksAccount = ''
      this.queryPageList()
    },
    handleCloseDetail(){
      this.addDialogVisible = false
      this.editDialogVisible = false
      this.queryPageList()
    },
    //新增
    handleAddRule() {
      this.addDialogVisible = true
      this.editForm = {}
    },
    //编辑
    handleEdit(index, row) {
      this.editDialogVisible = true
      this.editForm = row
      this.oldDataAccount = row.dataworksAccount
      this.oldProjectName = row.projectName
    },
    //提交新增
    handleSendAdd() {
      this.$refs['editFormRef'].validate(valid => {
        if (valid) {
          request({
            url: '/datalab/dataworksAcProject/add',
            method: 'post',
            data: {
              dataworksAccount: this.editForm.dataworksAccount,
              projectName: this.editForm.projectName,
            }
          }).then(res => {
            if (res.returnCode === '200') {
              this.$message({
                message: '新增成功',
                type: 'success'
              })
            } else {
              this.$message({
                message: '新增失败',
                type: 'error'
              })
            }
            this.addDialogVisible = false
            this.queryPageList()
          })
        } else {
          return false
        }
      })
    },
    //提交编辑
    handleSendEdit() {
      this.$refs['editFormRef'].validate(valid => {
        if (valid) {
          request({
            url: '/datalab/dataworksAcProject/update',
            method: 'post',
            data: {
              dataworksAccount: this.editForm.dataworksAccount,
              projectName: this.editForm.projectName,
              oldDataAccount: this.oldDataAccount,
              oldProjectName: this.oldProjectName
            }
          }).then(res => {
            if (res.returnCode === '200') {
              this.$message({
                message: '修改成功',
                type: 'success'
              })
            } else {
              this.$message({
                message: '修改失败',
                type: 'error'
              })
            }
            this.editDialogVisible = false
            this.queryPageList()
          })
        } else {
          return false
        }
      })
    },
    //删除
    handleDelete(index, row) {
      this.$confirm('确定删除dataworks账户吗?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        request({
          url: '/datalab/dataworksAcProject/delete',
          method: 'post',
          data: {
            dataworksAccount: row.dataworksAccount,
            projectName: row.projectName
          }
        }).then(res => {
          if (res.returnCode === '200') {
            this.$message({
              type: 'success',
              message: '删除成功!'
            });
            this.queryPageList()
          } else {
            this.$message({
              type: 'error',
              message: res.returnMsg
            });
          }
        })
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        });
      });
    },
    //查看对应UM用户
    showRelate(index, row) {
      this.$router.push({
        path: 'umRelate',
        query: {
          dataworksAccount: row.dataworksAccount,
          projectName: row.projectName
        }
      })
    },
  },
  mounted() {
    this.queryPageList()
  },
}
