import { mixinsFunctions } from "@/mixins/mixinsFunc";
export default {
  mixins: [mixinsFunctions],
  data() {
    return {
      // basicinfo
      basicinfo: {
        indexName: "/api/test",
        interfaceAds: "/api/test",
        apiDescription: "用于查询XXXXXXXXXXX数据",
        reqType: "get", //reqType
        respType: "JSON", //respType
        owner: "AA",
        application: "ind_test",
        myCrtTime: "2022-01-01", //createTime
        myUpdTime: "2022-04-12", //updateTime
        online: "是",
        catalogue1: "个贷",
        catalogue2: "网贷",
        catalogue1Name: "产品/贷款/个贷",
        catalogue2Name: "产品/贷款/网贷",
      },
      fielddataList: [],
      resdataList: [],
      respropList: [
        {
          prop: "name",
          label: "参数名称",
          align: "center",
        },
        {
          prop: "type",
          label: "参数类型",
          align: "center",
        },
        {
          prop: "description",
          label: "参数说明",
          align: "center",
        },
        {
          prop: "defaultValue",
          label: "默认值",
          align: "center",
        },
        {
          prop: "isPass",
          label: "是否必传",
          align: "center",
        },
      ],
      detailsView: false,
      id: "",
      assetsState: "",
      // 目录树
      catalogueList: [],
      cataValue: [],
    };
  },
  methods: {
    initData() {
      if (this.$route.name === "API详情") {
        this.detailsView = true;
      }
      // 目录树
      this.$api.getAllCatalogueList({}).then((res) => {
        this.catalogueList = res;
      });
      // 数据
      this.id = this.$route.query.id; // 通过路由传递id
      this.$api
        .getApiById({
          id: this.id,
          isRecord: this.detailsView,
        })
        .then((res) => {
          this.basicinfo = res;
          // 发布状态
          this.assetsState = this.basicinfo.assetsState;
          // 返回的目录  -- 渲染
          let temp = this.basicinfo.catalogues;
          this.cataValue= [];
          this.fielddataList = this.basicinfo.reqParams || [];
          this.resdataList = this.basicinfo.respParams || [];
          for (let key in temp) {
            key = key.split("/");
            this.cataValue.push({ id: key });
          }
        });
    },
    handleChange(val) {
      console.log(val);
    },
    confirmModify() {
      let list = JSON.parse(JSON.stringify(this.cataValue));
      list = list.map((item) => {
        return (item = item.id.join("/"));
      });
      let listSet = Array.from(new Set(list));
      if (listSet.length !== list.length) {
        this.$message.error("添加目录不能重复");
      } else {
        this.$api
          .updateAssetApi({
            id: this.id,
            list: list,
            assetsState: this.assetsState,
          })
          .then((res) => {
            this.$message.success("修改成功!");
            this.closeThisPage();
          });
      }
    },
    deleteCatalogue(index) {
      this.cataValue.splice(index, 1);
    },
    addCatalogue() {
      if (this.cataValue.length > 4) {
        this.$message.warning("最多添加5条目录");
      } else {
        this.cataValue.push({
          id: [],
        });
      }
    },
  },
};
